var body = $input.first().json.body || $input.first().json;

// === VALIDATION STRICTE ===
var UUID_RE = /^[0-9a-f]{8}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{12}$/i;
var DATE_RE = /^\d{4}-\d{2}-\d{2}$/;
var SAFE_TEXT = /^[\w\s\-àâäéèêëïîôùûüÿçÀÂÄÉÈÊËÏÎÔÙÛÜŸÇ.,;:!?()\/#&€@+'\"°%*]+$/;
var VALID_JOURNAL_CODES = ['VE', 'AC', 'BQ', 'CA', 'OD', 'AN', 'EXT'];
var VALID_SOURCES = ['facture', 'avoir', 'paiement', 'manuelle', 'import'];

if (!body.tenantId || !UUID_RE.test(body.tenantId)) throw new Error('tenantId requis (UUID valide)');
if (!body.journalCode || !VALID_JOURNAL_CODES.includes(body.journalCode)) throw new Error('journalCode invalide: ' + VALID_JOURNAL_CODES.join(', '));
if (!body.libelle || !SAFE_TEXT.test(body.libelle)) throw new Error('libelle requis (caractères spéciaux non autorisés)');
if (!body.lignes || !Array.isArray(body.lignes) || body.lignes.length < 2) throw new Error('Au moins 2 lignes requises');

var dateEcriture = body.dateEcriture || new Date().toISOString().split('T')[0];
if (!DATE_RE.test(dateEcriture)) throw new Error('dateEcriture format invalide (YYYY-MM-DD)');

var sourceAuto = body.sourceAuto || 'manuelle';
if (!VALID_SOURCES.includes(sourceAuto)) throw new Error('sourceAuto invalide');
if (body.sourceId && !UUID_RE.test(body.sourceId)) throw new Error('sourceId doit être un UUID valide');

var lignes = body.lignes;
var totalDebit = lignes.reduce(function(s, l) { return s + (parseFloat(l.debit) || 0); }, 0);
var totalCredit = lignes.reduce(function(s, l) { return s + (parseFloat(l.credit) || 0); }, 0);
if (Math.abs(totalDebit - totalCredit) > 0.01) {
  throw new Error('Ecriture desequilibree: debit ' + totalDebit.toFixed(2) + ' != credit ' + totalCredit.toFixed(2));
}

return [{ json: {
  tenantId: body.tenantId,
  journalCode: body.journalCode,
  dateEcriture: dateEcriture,
  libelle: body.libelle,
  referenceDoc: body.referenceDoc || body.reference || null,
  sourceAuto: sourceAuto,
  sourceId: body.sourceId || null,
  lignes: JSON.stringify(lignes)
} }];

var exerciceResult = $('05. Find Exercice').all().map(function(i) { return i.json; }).filter(function(e) { return e.id; });
if (exerciceResult.length === 0) throw new Error('Aucun exercice ouvert pour cette date');

var exerciceId = exerciceResult[0].id;
var nextNum = parseInt($('06. Next Number').first().json.next_num) || 1;
var year = new Date().getFullYear();
var numeroPiece = 'EC-' + year + '-' + String(nextNum).padStart(6, '0');
var p = $('02. Parser').first().json;

return [{ json: {
  tenantId: p.tenantId,
  exerciceId: exerciceId,
  journalCode: p.journalCode,
  numeroPiece: numeroPiece,
  dateEcriture: p.dateEcriture,
  libelle: p.libelle,
  referenceDoc: p.referenceDoc,
  sourceAuto: p.sourceAuto,
  sourceId: p.sourceId,
  lignes: p.lignes
} }];

function esc(v) { return v == null ? 'NULL' : "'" + String(v).replace(/'/g, "''") + "'"; }

var p = $input.first().json;

var sourceIdVal = p.sourceId ? esc(p.sourceId) + '::uuid' : 'NULL';
var refDocVal = p.referenceDoc ? esc(p.referenceDoc) : 'NULL';
var sourceAutoVal = p.sourceAuto ? esc(p.sourceAuto) : 'NULL';

var query = 'INSERT INTO ecritures_comptables (id, tenant_id, exercice_id, journal_code, numero_piece, date_ecriture, libelle, reference_doc, source_auto, source_id, statut, updated_at) VALUES (gen_random_uuid(), ' + esc(p.tenantId) + '::uuid, ' + esc(p.exerciceId) + '::uuid, ' + esc(p.journalCode) + ', ' + esc(p.numeroPiece) + ', ' + esc(p.dateEcriture) + '::date, ' + esc(p.libelle) + ', ' + refDocVal + ', ' + sourceAutoVal + ', ' + sourceIdVal + ', ' + esc('brouillon') + ', NOW()) RETURNING id, numero_piece, date_ecriture, libelle, statut;';

return [{ json: { query: query, lignes: p.lignes } }];

function esc(v) { return v == null ? 'NULL' : "'" + String(v).replace(/'/g, "''") + "'"; }

var ecriture = $('09. Insert Ecriture').first().json;
var lignes = JSON.parse($('08. Build Insert').first().json.lignes);

var COMPTE_RE = /^[0-9]{3,10}$/;

if (!ecriture.id) throw new Error('ID ecriture invalide');

var values = lignes.map(function(l, i) {
  var debit = parseFloat(l.debit) || 0;
  var credit = parseFloat(l.credit) || 0;
  var numeroCompte = String(l.numeroCompte || '').trim();
  if (!COMPTE_RE.test(numeroCompte)) throw new Error('Ligne ' + i + ': numero de compte invalide (' + numeroCompte + ')');
  var libelle = String(l.libelle || '').substring(0, 255);

  return '(gen_random_uuid(), ' + esc(ecriture.id) + '::uuid, ' + esc(numeroCompte) + ', ' + esc(libelle) + ', ' + debit + ', ' + credit + ', ' + i + ')';
}).join(',\n');

var query = 'INSERT INTO lignes_ecritures (id, ecriture_id, numero_compte, libelle_ligne, debit, credit, ordre) VALUES\n' + values + '\nRETURNING id, numero_compte, libelle_ligne, debit, credit, ordre;';

return [{ json: { ecritureId: ecriture.id, query: query } }];

var ecriture = $('09. Insert Ecriture').first().json;
var lignes = $('11. Insert Lines').all().map(function(i) { return i.json; }).filter(function(l) { return l.id; });

return [{ json: {
  success: true,
  ecriture: {
    id: ecriture.id,
    numeroPiece: ecriture.numero_piece,
    dateEcriture: ecriture.date_ecriture,
    libelle: ecriture.libelle,
    statut: ecriture.statut,
    lignes: lignes.map(function(l) { return ({
      id: l.id,
      numeroCompte: l.numero_compte,
      libelle: l.libelle_ligne,
      debit: parseFloat(l.debit) || 0,
      credit: parseFloat(l.credit) || 0
    }); })
  }
} }];
