const body = $input.first().json.body || $input.first().json;
if (!body.tenantId) throw new Error('tenantId is required');
if (!body.journalCode) throw new Error('journalCode is required');
if (!body.libelle) throw new Error('libelle is required');
if (!body.lignes || !Array.isArray(body.lignes) || body.lignes.length < 2) throw new Error('Au moins 2 lignes requises');

const dateEcriture = body.dateEcriture || new Date().toISOString().split('T')[0];
const lignes = body.lignes;

// Vérifier équilibre débit = crédit
const totalDebit = lignes.reduce((s, l) => s + (parseFloat(l.debit) || 0), 0);
const totalCredit = lignes.reduce((s, l) => s + (parseFloat(l.credit) || 0), 0);
if (Math.abs(totalDebit - totalCredit) > 0.01) {
  throw new Error(`Écriture déséquilibrée: débit ${totalDebit.toFixed(2)} ≠ crédit ${totalCredit.toFixed(2)}`);
}

return [{ json: {
  tenantId: body.tenantId,
  journalCode: body.journalCode,
  dateEcriture,
  dateEcheance: body.dateEcheance || null,
  libelle: body.libelle,
  reference: body.reference || null,
  typePiece: body.typePiece || 'manuelle',
  entiteType: body.entiteType || null,
  entiteId: body.entiteId || null,
  montantTotal: totalDebit,
  source: body.source || 'manuelle',
  lignes: JSON.stringify(lignes)
} }];

const exerciceResult = $('05. Find Exercice').all().map(i => i.json).filter(e => e.id);
if (exerciceResult.length === 0) throw new Error('Aucun exercice ouvert pour cette date');

const exerciceId = exerciceResult[0].id;
const nextNum = parseInt($('06. Next Number').first().json.next_num) || 1;
const year = new Date().getFullYear();
const numeroPiece = `EC-${year}-${String(nextNum).padStart(6, '0')}`;
const p = $('02. Parser').first().json;

return [{ json: { ...p, exerciceId, numeroPiece } }];

const ecriture = $('08. Insert Ecriture').first().json;
const lignes = JSON.parse($('02. Parser').first().json.lignes);

// Build INSERT pour toutes les lignes
const values = lignes.map((l, i) => {
  const debit = parseFloat(l.debit) || 0;
  const credit = parseFloat(l.credit) || 0;
  const libelle = (l.libelle || '').replace(/'/g, "''");
  return `('${ecriture.id}', '${l.numeroCompte}', '${libelle}', ${debit}, ${credit}, ${i})`;
}).join(',\n');

return [{ json: { ecritureId: ecriture.id, insertQuery: `INSERT INTO lignes_ecritures (ecriture_id, numero_compte, libelle, debit, credit, ordre) VALUES ${values} RETURNING id, numero_compte, libelle, debit, credit, ordre;` } }];

const ecriture = $('08. Insert Ecriture').first().json;
const lignes = $('10. Insert Lines').all().map(i => i.json).filter(l => l.id);

return [{ json: {
  success: true,
  ecriture: {
    id: ecriture.id,
    numeroPiece: ecriture.numero_piece,
    dateEcriture: ecriture.date_ecriture,
    libelle: ecriture.libelle,
    montantTotal: parseFloat(ecriture.montant_total),
    statut: ecriture.statut,
    lignes: lignes.map(l => ({
      id: l.id,
      numeroCompte: l.numero_compte,
      libelle: l.libelle,
      debit: parseFloat(l.debit) || 0,
      credit: parseFloat(l.credit) || 0
    }))
  }
} }];
