const body = $input.first().json.body || $input.first().json;

// === VALIDATION STRICTE (anti-injection SQL) ===
const UUID_RE = /^[0-9a-f]{8}-[0-9a-f]{4}-4[0-9a-f]{3}-[89ab][0-9a-f]{3}-[0-9a-f]{12}$/i;
const DATE_RE = /^\d{4}-\d{2}-\d{2}$/;
const SAFE_TEXT = /^[\w\s\-àâäéèêëïîôùûüÿçÀÂÄÉÈÊËÏÎÔÙÛÜŸÇ.,;:!?()\/#&€@+'"°%*]+$/;
const VALID_JOURNAL_CODES = ['VE', 'AC', 'BQ', 'CA', 'OD', 'AN', 'EXT'];
const VALID_TYPES_PIECE = ['facture', 'avoir', 'reglement', 'manuelle', 'ouverture'];
const VALID_SOURCES = ['facture', 'avoir', 'reglement', 'manuelle', 'import'];
const VALID_ENTITE_TYPES = ['facture', 'avoir', 'devis', 'bon_commande', 'proforma', null];

// Escaping removed - values are validated by SAFE_TEXT regex and passed as params downstream

if (!body.tenantId || !UUID_RE.test(body.tenantId)) throw new Error('tenantId requis (UUID valide)');
if (!body.journalCode || !VALID_JOURNAL_CODES.includes(body.journalCode)) throw new Error('journalCode invalide: ' + VALID_JOURNAL_CODES.join(', '));
if (!body.libelle || !SAFE_TEXT.test(body.libelle)) throw new Error('libelle requis (caractères spéciaux non autorisés)');
if (!body.lignes || !Array.isArray(body.lignes) || body.lignes.length < 2) throw new Error('Au moins 2 lignes requises');

const dateEcriture = body.dateEcriture || new Date().toISOString().split('T')[0];
if (!DATE_RE.test(dateEcriture)) throw new Error('dateEcriture format invalide (YYYY-MM-DD)');
if (body.dateEcheance && !DATE_RE.test(body.dateEcheance)) throw new Error('dateEcheance format invalide');

const typePiece = body.typePiece || 'manuelle';
if (!VALID_TYPES_PIECE.includes(typePiece)) throw new Error('typePiece invalide');
const source = body.source || 'manuelle';
if (!VALID_SOURCES.includes(source)) throw new Error('source invalide');
if (body.entiteType && !VALID_ENTITE_TYPES.includes(body.entiteType)) throw new Error('entiteType invalide');
if (body.entiteId && !UUID_RE.test(body.entiteId)) throw new Error('entiteId doit être un UUID valide');

const lignes = body.lignes;
const totalDebit = lignes.reduce((s, l) => s + (parseFloat(l.debit) || 0), 0);
const totalCredit = lignes.reduce((s, l) => s + (parseFloat(l.credit) || 0), 0);
if (Math.abs(totalDebit - totalCredit) > 0.01) {
  throw new Error(`Écriture déséquilibrée: débit ${totalDebit.toFixed(2)} ≠ crédit ${totalCredit.toFixed(2)}`);
}

return [{ json: {
  tenantId: body.tenantId,
  journalCode: body.journalCode,
  dateEcriture,
  dateEcheance: body.dateEcheance || null,
  libelle: body.libelle,
  reference: body.reference || null,
  typePiece,
  entiteType: body.entiteType || null,
  entiteId: body.entiteId || null,
  montantTotal: totalDebit,
  source,
  lignes: JSON.stringify(lignes)
} }];

const exerciceResult = $('05. Find Exercice').all().map(i => i.json).filter(e => e.id);
if (exerciceResult.length === 0) throw new Error('Aucun exercice ouvert pour cette date');

const exerciceId = exerciceResult[0].id;
const nextNum = parseInt($('06. Next Number').first().json.next_num) || 1;
const year = new Date().getFullYear();
const numeroPiece = `EC-${year}-${String(nextNum).padStart(6, '0')}`;
const p = $('02. Parser').first().json;

return [{ json: { ...p, exerciceId, numeroPiece } }];

const ecriture = $('08. Insert Ecriture').first().json;
const lignes = JSON.parse($('02. Parser').first().json.lignes);

// Validation stricte des données des lignes
const UUID_RE = /^[0-9a-f]{8}-[0-9a-f]{4}-4[0-9a-f]{3}-[89ab][0-9a-f]{3}-[0-9a-f]{12}$/i;
const COMPTE_RE = /^[0-9]{3,10}$/;

if (!ecriture.id || !UUID_RE.test(ecriture.id)) throw new Error('ID écriture invalide');

const params = [];
let paramIdx = 1;

const values = lignes.map((l, i) => {
  const debit = parseFloat(l.debit) || 0;
  const credit = parseFloat(l.credit) || 0;
  if (!Number.isFinite(debit) || !Number.isFinite(credit)) throw new Error(`Ligne ${i}: montant invalide`);
  const numeroCompte = String(l.numeroCompte || '').trim();
  if (!COMPTE_RE.test(numeroCompte)) throw new Error(`Ligne ${i}: numéro de compte invalide (${numeroCompte})`);
  const libelle = String(l.libelle || '').substring(0, 255);

  params.push(ecriture.id, numeroCompte, libelle, debit, credit, i);
  const p = `($${paramIdx}, $${paramIdx+1}, $${paramIdx+2}, $${paramIdx+3}, $${paramIdx+4}, $${paramIdx+5})`;
  paramIdx += 6;
  return p;
}).join(',\n');

const query = `INSERT INTO lignes_ecritures (ecriture_id, numero_compte, libelle, debit, credit, ordre) VALUES ${values} RETURNING id, numero_compte, libelle, debit, credit, ordre;`;
return [{ json: { ecritureId: ecriture.id, query, params } }];

const ecriture = $('08. Insert Ecriture').first().json;
const lignes = $('10. Insert Lines').all().map(i => i.json).filter(l => l.id);

return [{ json: {
  success: true,
  ecriture: {
    id: ecriture.id,
    numeroPiece: ecriture.numero_piece,
    dateEcriture: ecriture.date_ecriture,
    libelle: ecriture.libelle,
    montantTotal: parseFloat(ecriture.montant_total),
    statut: ecriture.statut,
    lignes: lignes.map(l => ({
      id: l.id,
      numeroCompte: l.numero_compte,
      libelle: l.libelle,
      debit: parseFloat(l.debit) || 0,
      credit: parseFloat(l.credit) || 0
    }))
  }
} }];
