var body = $input.first().json.body || $input.first().json;
var UUID_RE = /^[0-9a-f]{8}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{12}$/i;
if (!body.tenantId || !UUID_RE.test(body.tenantId)) throw new Error('tenantId requis (UUID valide)');
if (!body.ecritureId && !body.id) throw new Error('ecritureId requis');
var ecritureId = body.ecritureId || body.id;
if (!UUID_RE.test(ecritureId)) throw new Error('ecritureId invalide (UUID)');
return [{ json: { tenantId: body.tenantId, ecritureId: ecritureId } }];

function esc(v) { return "'" + String(v).replace(/'/g, "''") + "'"; }
var p = $('02. Parser').first().json;

var ecritureQuery = "SELECT e.id, e.exercice_id, e.journal_code, e.numero_piece, e.date_ecriture, e.libelle, e.reference_doc, e.statut, e.source_auto, e.source_id, e.created_at, e.updated_at, j.libelle as journal_libelle, ex.code as exercice_code FROM ecritures_comptables e LEFT JOIN journaux_comptables j ON e.journal_code = j.code AND e.tenant_id = j.tenant_id LEFT JOIN exercices_comptables ex ON e.exercice_id = ex.id WHERE e.id = " + esc(p.ecritureId) + "::uuid AND e.tenant_id = " + esc(p.tenantId) + "::uuid;";

var lignesQuery = "SELECT le.id, le.numero_compte, le.libelle_ligne, le.debit, le.credit, le.lettrage, le.date_lettrage, le.ordre, pc.libelle as compte_libelle FROM lignes_ecritures le LEFT JOIN plan_comptable pc ON le.numero_compte = pc.numero_compte AND pc.tenant_id = " + esc(p.tenantId) + "::uuid WHERE le.ecriture_id = " + esc(p.ecritureId) + "::uuid ORDER BY le.ordre ASC;";

return [{ json: { ecritureQuery: ecritureQuery, lignesQuery: lignesQuery } }];

var ecritureArr = $('06. Get Ecriture').all().map(function(i) { return i.json; }).filter(function(e) { return e.id; });
if (ecritureArr.length === 0) return [{ json: { success: false, error: 'Écriture non trouvée' } }];

var e = ecritureArr[0];
var lignes = $('07. Get Lignes').all().map(function(i) { return i.json; }).filter(function(l) { return l.id; });

var totalDebit = lignes.reduce(function(s, l) { return s + (parseFloat(l.debit) || 0); }, 0);
var totalCredit = lignes.reduce(function(s, l) { return s + (parseFloat(l.credit) || 0); }, 0);

return [{ json: {
  success: true,
  ecriture: {
    id: e.id,
    exerciceId: e.exercice_id,
    exerciceCode: e.exercice_code || null,
    journalCode: e.journal_code,
    journalLibelle: e.journal_libelle || null,
    numeroPiece: e.numero_piece,
    dateEcriture: e.date_ecriture,
    libelle: e.libelle,
    referenceDoc: e.reference_doc || null,
    statut: e.statut,
    sourceAuto: e.source_auto || null,
    sourceId: e.source_id || null,
    totalDebit: totalDebit,
    totalCredit: totalCredit,
    montantTotal: Math.max(totalDebit, totalCredit),
    createdAt: e.created_at,
    updatedAt: e.updated_at,
    lignes: lignes.map(function(l) { return ({
      id: l.id,
      numeroCompte: l.numero_compte,
      compteLibelle: l.compte_libelle || null,
      libelleLigne: l.libelle_ligne || null,
      debit: parseFloat(l.debit) || 0,
      credit: parseFloat(l.credit) || 0,
      lettrage: l.lettrage || null,
      dateLettrage: l.date_lettrage || null,
      ordre: l.ordre
    }); })
  }
} }];
