var body = $input.first().json.body || $input.first().json;
if (!body.tenantId) throw new Error('tenantId is required');
var UUID_RE = /^[0-9a-f]{8}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{12}$/i;
if (!UUID_RE.test(body.tenantId)) throw new Error('tenantId invalide');
var today = new Date().toISOString().split('T')[0];
return [{ json: {
  tenantId: body.tenantId,
  dateFrom: body.dateFrom || null,
  dateTo: body.dateTo || today,
  compteFrom: body.compteFrom || null,
  compteTo: body.compteTo || null
} }];

function esc(v) { return "'" + String(v).replace(/'/g, "''") + "'"; }
var p = $('Parser').first().json;
var query = "SELECT le.numero_compte, pc.libelle as compte_libelle, e.date_ecriture, e.numero_piece, e.journal_code, le.libelle_ligne, le.debit, le.credit FROM lignes_ecritures le JOIN ecritures_comptables e ON le.ecriture_id = e.id LEFT JOIN plan_comptable pc ON le.numero_compte = pc.numero_compte AND pc.tenant_id = e.tenant_id WHERE e.tenant_id = " + esc(p.tenantId) + "::uuid AND e.statut = 'validee'";
if (p.dateFrom) query += " AND e.date_ecriture >= " + esc(p.dateFrom) + "::date";
if (p.dateTo) query += " AND e.date_ecriture <= " + esc(p.dateTo) + "::date";
if (p.compteFrom) query += " AND le.numero_compte >= " + esc(p.compteFrom);
if (p.compteTo) query += " AND le.numero_compte <= " + esc(p.compteTo);
query += " ORDER BY le.numero_compte ASC, e.date_ecriture ASC";
return [{ json: { query: query } }];

var items = $('Query Grand Livre').all().map(function(i) { return i.json; }).filter(function(c) { return c.numero_compte; });
var p = $('Parser').first().json;
var compteMap = {};
var totalDebit = 0;
var totalCredit = 0;
for (var i = 0; i < items.length; i++) {
  var row = items[i];
  var nc = row.numero_compte || '';
  var d = parseFloat(row.debit) || 0;
  var c = parseFloat(row.credit) || 0;
  totalDebit += d;
  totalCredit += c;
  if (!compteMap[nc]) {
    compteMap[nc] = { numeroCompte: nc, libelleCompte: row.compte_libelle || 'Sans libellé', totalDebit: 0, totalCredit: 0, solde: 0, lignes: [] };
  }
  compteMap[nc].totalDebit += d;
  compteMap[nc].totalCredit += c;
  compteMap[nc].lignes.push({ date: row.date_ecriture, journalCode: row.journal_code, numeroEcriture: row.numero_piece || null, libelle: row.libelle_ligne, debit: d, credit: c });
}
var comptes = Object.values(compteMap).map(function(compte) {
  compte.solde = compte.totalDebit - compte.totalCredit;
  var rb = 0;
  compte.lignes.forEach(function(l) { rb += (l.debit - l.credit); l.soldeCumule = rb; });
  return compte;
});
return [{ json: {
  success: true,
  comptes: comptes,
  totaux: { totalDebit: totalDebit, totalCredit: totalCredit },
  periode: { dateFrom: p.dateFrom || null, dateTo: p.dateTo || null }
} }];
