const body = $input.first().json.body || $input.first().json;
if (!body.tenantId) throw new Error('tenantId is required');
const exerciceCode = body.exerciceCode || new Date().getFullYear().toString();
const dateDebut = body.dateDebut || `${exerciceCode}-01-01`;
const dateFin = body.dateFin || `${exerciceCode}-12-31`;
return [{ json: { tenantId: body.tenantId, exerciceCode, dateDebut, dateFin } }];

const exercice = $('05. Create Exercice').all().map(i => i.json).filter(e => e.id);
const nbComptes = parseInt($('08. Count Comptes').first().json.nb_comptes) || 0;

return [{ json: {
  success: true,
  message: `Comptabilité initialisée: ${nbComptes} comptes PCG, 5 journaux, exercice ${$('02. Parser').first().json.exerciceCode}`,
  exercice: exercice[0] || { code: $('02. Parser').first().json.exerciceCode, status: 'already_exists' },
  nbComptes,
  journaux: ['VE - Ventes', 'AC - Achats', 'BQ - Banque', 'OD - Opérations Diverses', 'AN - À-Nouveaux']
}}];
