var body = $input.first().json.body || $input.first().json;
if (!body.tenantId) throw new Error('tenantId is required');
var UUID_RE = /^[0-9a-f]{8}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{12}$/i;
if (!UUID_RE.test(body.tenantId)) throw new Error('tenantId invalide');
var ligneIds = body.ligneIds || [];
if (!Array.isArray(ligneIds) || ligneIds.length < 2) throw new Error('Au moins 2 ligneIds requis');
var allValid = ligneIds.every(function(id) { return UUID_RE.test(id); });
if (!allValid) throw new Error('ligneIds invalides');
return [{ json: { tenantId: body.tenantId, numeroCompte: body.numeroCompte || null, ligneIds: ligneIds, valid: true } }];

function esc(v) { return "'" + String(v).replace(/'/g, "''") + "'"; }
var p = $('Parser').first().json;
var inClause = p.ligneIds.map(function(id) { return esc(id) + "::uuid"; }).join(",");
var query = "SELECT id, numero_compte, libelle_ligne, debit, credit, lettrage FROM lignes_ecritures WHERE tenant_id = " + esc(p.tenantId) + "::uuid AND id IN (" + inClause + ") ORDER BY numero_compte ASC";
return [{ json: { query: query, ligneIds: p.ligneIds, tenantId: p.tenantId } }];

var items = $('Get Lignes').all().map(function(i) { return i.json; }).filter(function(r) { return r.id; });
if (items.length < 2) throw new Error('Au moins 2 lignes comptables requises');
var totalDebits = 0, totalCredits = 0;
items.forEach(function(l) { totalDebits += (parseFloat(l.debit) || 0); totalCredits += (parseFloat(l.credit) || 0); });
var difference = Math.abs(totalDebits - totalCredits);
if (difference > 0.05) throw new Error('Écart débit/crédit: ' + difference.toFixed(2));
var letterageCode = 'LTR_' + Date.now() + '_' + Math.random().toString(36).substring(2, 8).toUpperCase();
return [{ json: { lignes: items, totalDebits: totalDebits, totalCredits: totalCredits, difference: difference, letterageCode: letterageCode } }];

function esc(v) { return "'" + String(v).replace(/'/g, "''") + "'"; }
var v = $('Verify Balance').first().json;
var tenantId = $('Parser').first().json.tenantId;
var now = new Date().toISOString();
var inClause = v.lignes.map(function(l) { return esc(l.id) + "::uuid"; }).join(",");
var updateQuery = "UPDATE lignes_ecritures SET lettrage = " + esc(v.letterageCode) + ", date_lettrage = " + esc(now) + "::timestamp WHERE id IN (" + inClause + ") AND tenant_id = " + esc(tenantId) + "::uuid";
var insertQuery = "INSERT INTO lettrages (tenant_id, lettrage_code, date_lettrage, nombre_lignes, montant_total) VALUES (" + esc(tenantId) + "::uuid, " + esc(v.letterageCode) + ", " + esc(now) + "::timestamp, " + v.lignes.length + ", " + v.totalDebits + ")";
return [{ json: { updateQuery: updateQuery, insertQuery: insertQuery, letterageCode: v.letterageCode, ligneCount: v.lignes.length } }];

var bu = $('Build Update').first().json;
var vb = $('Verify Balance').first().json;
return [{ json: { success: true, letterageCode: bu.letterageCode, message: 'Lettrage applied successfully', ligneCount: bu.ligneCount, totalDebits: vb.totalDebits, totalCredits: vb.totalCredits } }];
