var body = $input.first().json.body || $input.first().json;
if (!body.tenantId) throw new Error('tenantId is required');
var UUID_RE = /^[0-9a-f]{8}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{12}$/i;
if (!UUID_RE.test(body.tenantId)) throw new Error('tenantId invalide');
var today = new Date().toISOString().split('T')[0];
return [{ json: {
  tenantId: body.tenantId,
  dateFrom: body.dateFrom || null,
  dateTo: body.dateTo || today,
  typeDeclaration: body.typeDeclaration || 'mensuelle'
} }];

function esc(v) { return "'" + String(v).replace(/'/g, "''") + "'"; }
var p = $('Parser').first().json;
var dateFilter = '';
if (p.dateFrom) dateFilter += ' AND e.date_ecriture >= ' + esc(p.dateFrom) + '::date';
if (p.dateTo) dateFilter += ' AND e.date_ecriture <= ' + esc(p.dateTo) + '::date';

var queryTVAComptes = "SELECT le.numero_compte, pc.libelle as account_name, COALESCE(SUM(le.debit), 0) as total_debit, COALESCE(SUM(le.credit), 0) as total_credit, COALESCE(SUM(le.debit), 0) - COALESCE(SUM(le.credit), 0) as solde FROM lignes_ecritures le JOIN ecritures_comptables e ON le.ecriture_id = e.id LEFT JOIN plan_comptable pc ON le.numero_compte = pc.numero_compte AND pc.tenant_id = e.tenant_id WHERE e.tenant_id = " + esc(p.tenantId) + "::uuid AND e.statut = 'validee' AND (le.numero_compte LIKE '44571%' OR le.numero_compte LIKE '44566%' OR le.numero_compte LIKE '44562%')" + dateFilter + " GROUP BY le.numero_compte, pc.libelle ORDER BY le.numero_compte";

var queryBasesHT = "SELECT SUBSTRING(le.numero_compte, 1, 1) as class, COALESCE(SUM(CASE WHEN le.numero_compte LIKE '6%' THEN (le.debit - le.credit) ELSE 0 END), 0) as total_achats, COALESCE(SUM(CASE WHEN le.numero_compte LIKE '7%' THEN (le.credit - le.debit) ELSE 0 END), 0) as total_ventes FROM lignes_ecritures le JOIN ecritures_comptables e ON le.ecriture_id = e.id WHERE e.tenant_id = " + esc(p.tenantId) + "::uuid AND e.statut = 'validee' AND (le.numero_compte LIKE '6%' OR le.numero_compte LIKE '7%')" + dateFilter + " GROUP BY class";

return [{ json: { queryTVAComptes: queryTVAComptes, queryBasesHT: queryBasesHT } }];

function inferTauxFromAccount(accountNumber) {
  if (!accountNumber) return 20;
  var suffix = accountNumber.slice(-2);
  var suffixNum = parseInt(suffix);
  if (suffixNum === 10 || suffixNum === 11) return 20;
  if (suffixNum === 12) return 10;
  if (suffixNum === 13) return 5.5;
  if (suffixNum === 14) return 2.1;
  return 20;
}
var tvaItems = $('Query TVA Comptes').all().map(function(i) { return i.json; }).filter(function(r) { return r.numero_compte; });
var basesItems = $('Query Bases HT').all().map(function(i) { return i.json; });
var p = $('Parser').first().json;
var tvaCollectee = [], tvaDeductible = [];
var totalCollectee = 0, totalDeductible = 0;
var basesData = basesItems.length > 0 ? basesItems[0] : {};
var totalAchats = Math.abs(parseFloat(basesData.total_achats) || 0);
var totalVentes = Math.abs(parseFloat(basesData.total_ventes) || 0);
tvaItems.forEach(function(row) {
  var nc = row.numero_compte || '';
  var montantTVA = Math.abs(parseFloat(row.solde) || 0);
  var taux = inferTauxFromAccount(nc);
  var baseHT = montantTVA > 0 ? Math.round((montantTVA / (taux / 100)) * 100) / 100 : 0;
  if (nc.startsWith('44571')) {
    totalCollectee += montantTVA;
    tvaCollectee.push({ taux: taux, baseHT: baseHT, montantTVA: Math.round(montantTVA * 100) / 100, compteCollecte: nc });
  } else if (nc.startsWith('44566') || nc.startsWith('44562')) {
    totalDeductible += montantTVA;
    tvaDeductible.push({ taux: taux, baseHT: baseHT, montantTVA: Math.round(montantTVA * 100) / 100, compteDeductible: nc });
  }
});
totalCollectee = Math.round(totalCollectee * 100) / 100;
totalDeductible = Math.round(totalDeductible * 100) / 100;
var tvaAPayer = totalCollectee - totalDeductible;
var result = { success: true, periode: { dateFrom: p.dateFrom, dateTo: p.dateTo }, typeDeclaration: p.typeDeclaration, totalCollectee: totalCollectee, totalDeductible: totalDeductible };
if (tvaCollectee.length > 0) result.tvaCollectee = tvaCollectee;
if (tvaDeductible.length > 0) result.tvaDeductible = tvaDeductible;
if (tvaAPayer > 0) result.tvaAPayer = tvaAPayer;
else if (tvaAPayer < 0) result.creditTVA = Math.abs(tvaAPayer);
return [{ json: result }];
