const body = $input.first().json.body || $input.first().json;

if (!body.tenantId || !body.devisId) {
  throw new Error('tenantId and devisId are required');
}

return [{ json: { tenantId: body.tenantId, devisId: body.devisId } }];

const devis = $('05. Get Devis').first().json;
const nextNum = $('06. Get Next BDC Number').first().json.next_num || 1;

if (!devis || !devis.id) {
  throw new Error('Devis not found or not in acceptee status');
}

const year = new Date().getFullYear();
const numeroBdc = 'BDC-' + year + '-' + String(nextNum).padStart(6, '0');
const now = new Date().toISOString();
const validite = new Date(Date.now() + 30 * 24 * 60 * 60 * 1000).toISOString();

return [{ json: {
  devisId: devis.id,
  tenantId: devis.tenant_id,
  clientFinalId: devis.client_final_id,
  numeroDevis: devis.numero_devis,
  numeroBdc,
  dateBdc: now,
  dateValidite: validite,
  montantHt: devis.montant_ht,
  montantTtc: devis.montant_ttc,
  tvaTaux: devis.tva_taux,
  description: devis.description ? 'Devis ' + devis.numero_devis + ' - ' + devis.description : 'Depuis devis ' + devis.numero_devis,
  modePaiement: devis.mode_paiement
} }];

// Original prepare logic
const _origFn = function() {
const bdc = $('08. Create BdC').first().json;
const lines = $('09. Get Devis Lines').all().map(item => item.json);

return [{ json: {
  bdcId: bdc.id,
  lines,
  hasLines: lines.length > 0
} }];
};
const _origResult = _origFn();
const prev = _origResult[0].json;

const items = prev.lines || [];

if (!prev.hasLines || items.length === 0) {
  return [{ json: { ...prev, query: 'SELECT 1', params: [] } }];
}

const params = [];
let idx = 1;

const parentId = prev.bdcId;
const values = items.map((item, i) => {
  const codeArticle = item.code_article || null;
  const designation = String(item.designation || '').substring(0, 500);
  const quantite = parseFloat(item.quantite) || 0;
  const prixUnitaireHt = parseFloat(item.prix_unitaire_ht) || 0;
  const totalHt = parseFloat(item.total_ht) || 0;
  const ordre = parseInt(item.ordre) || i;

  params.push(parentId, codeArticle, designation, quantite, prixUnitaireHt, totalHt, ordre);
  const p = `(gen_random_uuid(), $$${idx}, $$${idx+1}, $$${idx+2}, $$${idx+3}, $$${idx+4}, $$${idx+5}, $$${idx+6})`;
  idx += 7;
  return p;
}).join(',
');

const query = `INSERT INTO bon_commande_lines (id, bon_commande_id, code_article, designation, quantite, prix_unitaire_ht, total_ht, ordre) VALUES ${values} RETURNING id, bon_commande_id, code_article, designation, quantite, prix_unitaire_ht, total_ht, ordre`;

return [{ json: { ...prev, query, params } }];

const bdc = $('08. Create BdC').first().json;
const prepData = $('07. Prepare BdC Data').first().json;

return [{ json: {
  bdc_id: bdc.id,
  numero_bdc: bdc.numero_bdc,
  devis_id: prepData.devisId,
  numero_devis: prepData.numeroDevis
} }];
