const body = $input.first().json.body || $input.first().json;

if (!body.tenantId || !body.devisId) {
  throw new Error('tenantId and devisId are required');
}

return [{ json: { tenantId: body.tenantId, devisId: body.devisId } }];

const devis = $('05. Get Devis').first().json;
const nextNum = $('06. Get Next BDC Number').first().json.next_num || 1;

if (!devis || !devis.id) {
  throw new Error('Devis not found or not in acceptee status');
}

const year = new Date().getFullYear();
const numeroBdc = 'BDC-' + year + '-' + String(nextNum).padStart(6, '0');
const now = new Date().toISOString();
const validite = new Date(Date.now() + 30 * 24 * 60 * 60 * 1000).toISOString();

return [{ json: {
  devisId: devis.id,
  tenantId: devis.tenant_id,
  clientFinalId: devis.client_final_id,
  numeroDevis: devis.numero_devis,
  numeroBdc,
  dateBdc: now,
  dateValidite: validite,
  montantHt: devis.montant_ht,
  montantTtc: devis.montant_ttc,
  tvaTaux: devis.tva_taux,
  description: devis.description ? 'Devis ' + devis.numero_devis + ' - ' + devis.description : 'Depuis devis ' + devis.numero_devis,
  modePaiement: devis.mode_paiement
} }];

var bdc = $('08. Create BdC').first().json;
var rawLines = [];
try { rawLines = $('09. Get Devis Lines').all().map(function(item) { return item.json; }); } catch(e) { rawLines = []; }

function esc(v) { return v == null ? 'NULL' : "'" + String(v).replace(/'/g, "''") + "'"; }
function num(v) { var n = parseFloat(v); return isNaN(n) ? '0' : String(n); }

if (rawLines.length === 0) {
  return [{ json: { bdcId: bdc.id, hasLines: false, query: 'SELECT 1' } }];
}

var values = rawLines.map(function(item, i) {
  var q = num(item.quantite || 0);
  var p = num(item.prix_unitaire_ht || 0);
  var t = num(item.total_ht || (parseFloat(q) * parseFloat(p)));
  return '(gen_random_uuid(), ' + esc(bdc.id) + '::uuid, ' + esc(item.code_article || null) + ', ' + esc(item.designation || '') + ', ' + q + ', ' + p + ', ' + t + ', ' + i + ')';
}).join(', ');

var query = 'INSERT INTO bon_commande_lines (id, bon_commande_id, code_article, designation, quantite, prix_unitaire_ht, total_ht, ordre) VALUES ' + values + ' RETURNING id, bon_commande_id, code_article, designation, quantite, prix_unitaire_ht, total_ht, ordre';

return [{ json: { bdcId: bdc.id, hasLines: true, query: query } }];

const bdc = $('08. Create BdC').first().json;
const devis = $('12. Update Devis').first().json;

return [{ json: {
  bdc_id: bdc.id,
  numero_bdc: bdc.numero_bdc,
  devis_id: devis.id,
  numero_devis: devis.numero_devis,
  devis_statut: devis.statut
} }];
