var body = $input.first().json.body || $input.first().json;

if (!body.tenantId || !body.devisId) {
  throw new Error('tenantId and devisId are required');
}

return [{ json: { tenantId: body.tenantId, devisId: body.devisId } }];

var devis = $('05. Get Devis').first().json;
var nextNum = $('06. Get Next Invoice Number').first().json.next_num || 1;

if (!devis || !devis.id) {
  throw new Error('Devis not found or not in acceptee status');
}

var year = new Date().getFullYear();
var numeroFacture = 'INV-' + year + '-' + String(nextNum).padStart(6, '0');
var now = new Date().toISOString();
var echeance = new Date(Date.now() + 30 * 24 * 60 * 60 * 1000).toISOString();

return [{ json: {
  devisId: devis.id,
  tenantId: devis.tenant_id,
  clientFinalId: devis.client_final_id,
  numeroFacture,
  dateFacture: now,
  dateEcheance: echeance,
  montantHt: devis.montant_ht,
  montantTtc: devis.montant_ttc,
  tvaTaux: devis.tva_taux,
  description: devis.description,
  modePaiement: devis.mode_paiement
} }];

// Original prepare logic
var _origFn = function() {
var invoice = $('08. Create Invoice').first().json;
var lines = $('09. Get Devis Lines').all().map(function(item) { return item.json; });

return [{ json: {
  invoiceId: invoice.id,
  lines,
  hasLines: lines.length > 0
} }];
};
var _origResult = _origFn();
var prev = _origResult[0].json;

var items = prev.lines || [];

if (!prev.hasLines || items.length === 0) {
  return [{ json: { ...prev, query: 'SELECT 1', params: [] } }];
}

var params = [];
var idx = 1;

var parentId = prev.invoiceId;
var values = items.map(function(item, i) { return {
  var codeArticle = item.code_article || null;
  var designation = String(item.designation || '').substring(0, 500);
  var quantite = parseFloat(item.quantite) || 0;
  var prixUnitaireHt = parseFloat(item.prix_unitaire_ht) || 0;
  var totalHt = parseFloat(item.total_ht) || 0;
  var ordre = parseInt(item.ordre) || i;

  params.push(parentId, codeArticle, designation, quantite, prixUnitaireHt, totalHt, ordre);
  var p = `(gen_random_uuid(), $$${idx}, $$${idx+1}, $$${idx+2}, $$${idx+3}, $$${idx+4}, $$${idx+5}, $$${idx+6})`;
  idx += 7;
  return p;
}; }).join(',
');

var query = `INSERT INTO invoice_lines (id, invoice_id, code_article, designation, quantite, prix_unitaire_ht, total_ht, ordre) VALUES ${values} RETURNING id, invoice_id, code_article, designation, quantite, prix_unitaire_ht, total_ht, ordre`;

return [{ json: { ...prev, query, params } }];

var invoice = $('08. Create Invoice').first().json;
var devis = $('12. Update Devis').first().json;

return [{ json: {
  invoice_id: invoice.id,
  numero_facture: invoice.numero_facture,
  devis_id: devis.id,
  numero_devis: devis.numero_devis,
  devis_statut: devis.statut
} }];
