const raw = $input.first().json;
const payload = (raw && typeof raw === 'object' && raw.data && typeof raw.data === 'object') ? { ...raw.data, tenantId: raw.data.tenantId || raw.tenantId } : raw;

if (!payload.tenantId || !payload.clientFinalId) {
  throw new Error('tenantId et clientFinalId sont requis');
}

const lines = payload.lines || [];

// Calculer montantHt depuis les lignes si non fourni
let montantHt = parseFloat(payload.montantHt) || 0;
if (!payload.montantHt && lines.length > 0) {
  montantHt = lines.reduce((sum, l) => sum + (parseFloat(l.prixUnitaireHt) || 0) * (parseInt(l.quantite) || 1), 0);
}

const tvaTaux = parseFloat(payload.tvaTaux) || 20;
const montantTtc = montantHt * (1 + tvaTaux / 100);
const dateDevis = payload.dateDevis || new Date().toISOString().split('T')[0];
const dateValidite = payload.dateValidite || new Date(new Date().getTime() + 30 * 24 * 60 * 60 * 1000).toISOString().split('T')[0];
const description = payload.description || '';
const modePaiement = payload.modePaiement || 'virement';

return {
  tenantId: payload.tenantId,
  clientFinalId: payload.clientFinalId,
  montantHt: montantHt,
  montantTtc: Math.round(montantTtc * 100) / 100,
  tvaTaux: tvaTaux,
  dateDevis: dateDevis,
  dateValidite: dateValidite,
  description: description,
  modePaiement: modePaiement,
  lines: lines.map((l, i) => ({
    codeArticle: l.codeArticle || null,
    designation: l.designation || l.description || 'Article',
    quantite: parseInt(l.quantite) || 1,
    prixUnitaireHt: parseFloat(l.prixUnitaireHt) || parseFloat(l.prixUnitaire) || 0,
    ordre: l.ordre || i + 1
  }))
};

const nextNum = $('05. Get Next Number').first().next_num;
const numeroDevis = 'DEV-' + new Date().getFullYear() + '-' + String(nextNum).padStart(6, '0');

return {
  ...JSON.parse(JSON.stringify($json)),
  numeroDevis: numeroDevis
};

const devisId = $('07. Insert Devis').first().json.id;
const lines = $('02. Parser').first().json.lines || [];
const numeroDevis = $('06. Generate Number').first().json.numeroDevis;

if (lines.length === 0) {
  return {
    devisId: devisId,
    numeroDevis: numeroDevis,
    hasLines: false,
    insertLinesSql: ''
  };
}

// Construire un INSERT multi-lignes
const values = lines.map(line => {
  const totalHt = (parseFloat(line.prixUnitaireHt) || 0) * (parseInt(line.quantite) || 1);
  const codeArticle = line.codeArticle ? `'${line.codeArticle.replace(/'/g, "''")}'` : 'NULL';
  const designation = (line.designation || 'Article').replace(/'/g, "''");
  return `(gen_random_uuid(), '${devisId}', ${codeArticle}, '${designation}', ${parseInt(line.quantite) || 1}, ${parseFloat(line.prixUnitaireHt) || 0}, ${Math.round(totalHt * 100) / 100}, ${line.ordre || 0})`;
}).join(',\n  ');

const sql = `INSERT INTO devis_lines (id, devis_id, code_article, designation, quantite, prix_unitaire_ht, total_ht, ordre) VALUES\n  ${values}`;

return {
  devisId: devisId,
  numeroDevis: numeroDevis,
  hasLines: true,
  insertLinesSql: sql
};

return {
  success: true,
  message: 'Devis créé avec succès',
  devisId: $('08. Prepare Lines').first().json.devisId,
  numeroDevis: $('08. Prepare Lines').first().json.numeroDevis
};
