var body = $input.first().json.body || $input.first().json;

if (!body.tenantId) {
  throw new Error('tenantId is required');
}
if (!body.clientFinalId) {
  throw new Error('clientFinalId is required');
}

var montantHt = parseFloat(body.montantHt) || 0;
var tvaTaux = parseFloat(body.tvaTaux) || 20;
var montantTtc = Math.round(montantHt * (1 + tvaTaux / 100) * 100) / 100;

return [{ json: {
  tenantId: body.tenantId,
  clientFinalId: body.clientFinalId,
  montantHt,
  montantTtc,
  tvaTaux,
  dateDevis: body.dateDevis || new Date().toISOString(),
  dateValidite: body.dateValidite || null,
  description: body.description || null,
  modePaiement: body.modePaiement || null,
  lines: body.lines || []
} }];

var parser = $('02. Parser').first().json;
var nextNum = $('05. Get Next Number').first().json.next_num || 1;
var year = new Date().getFullYear();
var numeroDevis = 'DEV-' + year + '-' + String(nextNum).padStart(6, '0');

return [{ json: { ...parser, numeroDevis } }];

var d = $json;
function esc(v) { return v == null ? 'NULL' : "'" + String(v).replace(/'/g, "''") + "'"; }
function num(v) { var n = parseFloat(v); return isNaN(n) ? '0' : String(n); }

var query = 'INSERT INTO devis (id, tenant_id, client_final_id, numero_devis, date_devis, date_validite, montant_ht, montant_ttc, tva_taux, description, mode_paiement, statut, created_at, updated_at) VALUES (gen_random_uuid(), ' + esc(d.tenantId) + '::uuid, ' + esc(d.clientFinalId) + '::uuid, ' + esc(d.numeroDevis) + ', ' + esc(d.dateDevis) + ', ' + esc(d.dateValidite) + ', ' + num(d.montantHt) + ', ' + num(d.montantTtc) + ', ' + num(d.tvaTaux || 20) + ', ' + esc(d.description) + ', ' + esc(d.modePaiement) + ', ' + esc('brouillon') + ', NOW(), NOW()) RETURNING *';

return [{ json: { query: query, lines: d.lines || [] } }];

var devis = $('07. Insert Devis').first().json;
var parser = $('02. Parser').first().json;
var lines = parser.lines || [];

function esc(v) { return v == null ? 'NULL' : "'" + String(v).replace(/'/g, "''") + "'"; }
function num(v) { var n = parseFloat(v); return isNaN(n) ? '0' : String(n); }

if (lines.length === 0) {
  return [{ json: { devisId: devis.id, hasLines: false, query: 'SELECT 1' } }];
}

var values = lines.map(function(line, idx) {
  var q = num(line.quantite || 1);
  var p = num(line.prixUnitaireHt || line.prixUnitaireHT || 0);
  var t = num(parseFloat(q) * parseFloat(p));
  return '(gen_random_uuid(), ' + esc(devis.id) + '::uuid, ' + esc(line.codeArticle || null) + ', ' + esc(line.designation || '') + ', ' + q + ', ' + p + ', ' + t + ', ' + idx + ')';
}).join(', ');

var query = 'INSERT INTO devis_lines (id, devis_id, code_article, designation, quantite, prix_unitaire_ht, total_ht, ordre) VALUES ' + values + ' RETURNING id, devis_id, code_article, designation, quantite, prix_unitaire_ht, total_ht, ordre';

return [{ json: { devisId: devis.id, hasLines: true, query: query } }];
