const payload = $input.body;

if (!payload.tenantId || !payload.clientFinalId || !payload.montantHt || !payload.tvaTaux) {
  throw new Error('tenantId, clientFinalId, montantHt, tvaTaux are required');
}

const montantHt = parseFloat(payload.montantHt);
const tvaTaux = parseFloat(payload.tvaTaux) / 100;
const montantTtc = montantHt * (1 + tvaTaux);
const dateDevis = payload.dateDevis || new Date().toISOString().split('T')[0];
const dateValidite = payload.dateValidite || new Date(new Date().getTime() + 30 * 24 * 60 * 60 * 1000).toISOString().split('T')[0];
const description = payload.description || '';
const modePaiement = payload.modePaiement || 'virement';
const lines = payload.lines || [];

return {
  tenantId: payload.tenantId,
  clientFinalId: payload.clientFinalId,
  montantHt: montantHt,
  montantTtc: montantTtc,
  tvaTaux: payload.tvaTaux,
  dateDevis: dateDevis,
  dateValidite: dateValidite,
  description: description,
  modePaiement: modePaiement,
  lines: lines
};

const nextNum = $('05. Get Next Number').first().next_num;
const numeroDevis = 'DEV-' + new Date().getFullYear() + '-' + String(nextNum).padStart(6, '0');

return {
  ...JSON.parse(JSON.stringify($json)),
  numeroDevis: numeroDevis
};

const devisId = $('07. Insert Devis').first().id;
const lines = $json.lines || [];
const lineInserts = lines.map((line, index) => ({
  devisId: devisId,
  ligneNumero: index + 1,
  description: line.description || '',
  quantite: line.quantite || 1,
  prixUnitaire: parseFloat(line.prixUnitaire) || 0,
  montantHt: parseFloat(line.montantHt) || 0,
  tvaTaux: parseFloat(line.tvaTaux) || 0,
  montantTtc: parseFloat(line.montantTtc) || 0
}));

return {
  devisId: devisId,
  lineInserts: lineInserts
};

return {
  success: true,
  message: 'Devis created successfully',
  devisId: $('07. Insert Devis').first().id,
  numeroDevis: $json.numeroDevis
};
