var body = $input.first().json.body || $input.first().json;

if (!body.tenantId || !body.devisId) {
  throw new Error('tenantId and devisId are required');
}

return [{ json: { tenantId: body.tenantId, devisId: body.devisId } }];

var devisItem = $('05. Get Devis').first().json;
var rawLines = [];
try { rawLines = $('06. Get Lines').all().map(function(item) { return item.json; }); } catch(e) { rawLines = []; }

if (!devisItem || !devisItem.id) {
  return [{ json: { error: 'Devis not found' } }];
}

var d = devisItem;

var lines = rawLines.map(function(l) {
  return {
    id: l.id,
    devisId: l.devis_id,
    codeArticle: l.code_article || null,
    designation: l.designation || null,
    quantite: l.quantite,
    prixUnitaireHt: l.prix_unitaire_ht,
    prixUnitaireHT: l.prix_unitaire_ht,
    totalHt: l.total_ht,
    totalHT: l.total_ht,
    ordre: l.ordre
  };
});

var devis = {
  id: d.id,
  tenantId: d.tenant_id,
  tenant_id: d.tenant_id,
  clientFinalId: d.client_final_id,
  client_final_id: d.client_final_id,
  numeroDevis: d.numero_devis,
  numero_devis: d.numero_devis,
  dateDevis: d.date_devis,
  date_devis: d.date_devis,
  dateValidite: d.date_validite,
  date_validite: d.date_validite,
  montantHt: d.montant_ht,
  montant_ht: d.montant_ht,
  montantTtc: d.montant_ttc,
  montant_ttc: d.montant_ttc,
  tvaTaux: d.tva_taux,
  tva_taux: d.tva_taux,
  description: d.description,
  modePaiement: d.mode_paiement,
  mode_paiement: d.mode_paiement,
  statut: d.statut,
  invoiceId: d.invoice_id,
  invoice_id: d.invoice_id,
  lienPdf: d.lien_pdf,
  lien_pdf: d.lien_pdf,
  createdAt: d.created_at,
  created_at: d.created_at,
  updatedAt: d.updated_at,
  updated_at: d.updated_at,
  clientFinal: d.client_final_id ? {
    id: d.client_final_id,
    nom: d.client_nom || null,
    prenom: d.client_prenom || null,
    email: d.client_email || null,
    telephone: d.client_telephone || null,
    raisonSociale: d.client_raison_sociale || null,
    raison_sociale: d.client_raison_sociale || null
  } : null,
  lines: lines
};

return [{ json: devis }];
