const body = $input.first().json.body || $input.first().json;

if (!body.tenantId || !body.devisId) {
  throw new Error('tenantId and devisId are required');
}

return [{ json: { tenantId: body.tenantId, devisId: body.devisId } }];

const devisItem = $('05. Get Devis').first().json;
const lines = $('06. Get Lines').all().map(item => item.json);

if (!devisItem || !devisItem.id) {
  return [{ json: { error: 'Devis not found' } }];
}

const d = devisItem;

const devis = {
  id: d.id,
  tenant_id: d.tenant_id,
  client_final_id: d.client_final_id,
  numero_devis: d.numero_devis,
  date_devis: d.date_devis,
  date_validite: d.date_validite,
  montant_ht: d.montant_ht,
  montant_ttc: d.montant_ttc,
  tva_taux: d.tva_taux,
  description: d.description,
  mode_paiement: d.mode_paiement,
  statut: d.statut,
  invoice_id: d.invoice_id,
  lien_pdf: d.lien_pdf,
  created_at: d.created_at,
  updated_at: d.updated_at,
  clientFinal: d.client_final_id ? {
    id: d.client_final_id,
    nom: d.client_nom || null,
    prenom: d.client_prenom || null,
    email: d.client_email || null,
    telephone: d.client_telephone || null,
    raison_sociale: d.client_raison_sociale || null
  } : null,
  lines: lines
};

return [{ json: devis }];
