var body = $input.first().json.body || $input.first().json;

if (!body.tenantId) {
  throw new Error('tenantId is required');
}

var page = parseInt(body.page) || 1;
var limit = parseInt(body.limit) || 10;
var offset = (page - 1) * limit;
var statut = body.statut || null;
var clientFinalId = body.clientFinalId || null;

return [{ json: { tenantId: body.tenantId, page, limit, offset, statut, clientFinalId } }];

var devisItems = $('06. List Query').all().map(function(item) { return item.json; }).filter(function(d) { return d.id; });
var devisIds = devisItems.map(function(d) { return "'" + d.id + "'"; });
var idsClause = devisIds.length > 0 ? devisIds.join(',') : "'00000000-0000-0000-0000-000000000000'";
var query = 'SELECT id, devis_id, code_article, designation, quantite, prix_unitaire_ht, total_ht, ordre FROM devis_lines WHERE devis_id IN (' + idsClause + ') ORDER BY ordre ASC';
return [{ json: { query, devisIds: devisItems.map(function(d) { return d.id; }) } }];

var items = $('06. List Query').all().map(function(item) { return item.json; }).filter(function(d) { return d.id; });
var linesRaw = $('06c. Lines Query').all().map(function(item) { return item.json; }).filter(function(l) { return l.devis_id; });
var countResult = $('05. Count Query').first().json;
var parser = $('02. Parser').first().json;
var total = parseInt(countResult.total) || 0;
var totalPages = Math.ceil(total / parser.limit) || 1;

// Grouper les lignes par devis_id
var linesByDevisId = {};
for (var i = 0; i < linesRaw.length; i++) {
  var l = linesRaw[i];
  var did = l.devis_id;
  if (!linesByDevisId[did]) linesByDevisId[did] = [];
  linesByDevisId[did].push({
    id: l.id,
    devisId: l.devis_id,
    codeArticle: l.code_article || null,
    designation: l.designation || '',
    quantite: parseFloat(l.quantite) || 1,
    prixUnitaireHt: parseFloat(l.prix_unitaire_ht) || 0,
    totalHt: parseFloat(l.total_ht) || 0,
    ordre: parseInt(l.ordre) || 0
  });
}

var devis = items.map(function(d) { return ({
  id: d.id,
  tenant_id: d.tenant_id,
  client_final_id: d.client_final_id,
  numero_devis: d.numero_devis,
  date_devis: d.date_devis,
  date_validite: d.date_validite,
  montant_ht: d.montant_ht,
  montant_ttc: d.montant_ttc,
  tva_taux: d.tva_taux,
  description: d.description,
  mode_paiement: d.mode_paiement,
  statut: d.statut,
  invoice_id: d.invoice_id,
  lien_pdf: d.lien_pdf,
  created_at: d.created_at,
  updated_at: d.updated_at,
  clientFinal: d.client_final_id ? {
    id: d.client_final_id,
    nom: d.client_nom || null,
    prenom: d.client_prenom || null,
    email: d.client_email || null,
    telephone: d.client_telephone || null,
    raison_sociale: d.client_raison_sociale || null
  } : null,
  lines: linesByDevisId[d.id] || []
}); });

return [{ json: {
  devis,
  count: devis.length,
  total,
  page: parser.page,
  limit: parser.limit,
  totalPages
}}];
