var raw = $input.first().json;
var body = raw.body || raw;
var data = body.data || body;
var tenantId = String(body.tenantId || data.tenantId || '');

function esc(v) {
  return v == null ? '' : String(v).replace(/'/g, "''");
}

const uuidRegex = /^[0-9a-f]{8}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{12}$/i;
if (!uuidRegex.test(tenantId)) {
  throw new Error('Invalid tenantId');
}

const fields = [];
const values = [];
fields.push('tenant_id');
values.push('\'' + esc(tenantId) + '\'');

if (data.memberId) { fields.push('member_id'); values.push('\'' + esc(data.memberId) + '\''); }
if (data.type) { fields.push('type'); values.push('\'' + esc(data.type) + '\''); }
if (data.dateDebut) { fields.push('date_debut'); values.push('\'' + esc(data.dateDebut) + '\''); }
if (data.dateFin) { fields.push('date_fin'); values.push('\'' + esc(data.dateFin) + '\''); }
if (data.motif) { fields.push('motif'); values.push('\'' + esc(data.motif) + '\''); }
if (data.statut) { fields.push('statut'); values.push('\'' + esc(data.statut) + '\''); } else { fields.push('statut'); values.push('\'en_attente\''); }

const query = 'INSERT INTO equipe_absences (' + fields.join(', ') + ') VALUES (' + values.join(', ') + ') RETURNING id, tenant_id, member_id, type, date_debut, date_fin, motif, statut, created_at, updated_at';

return [{json: {query: query}}];

const rows = $input.first().json.queryResults || [];
if (rows.length === 0) {
  return [{json: {success: false, error: 'Failed to create'}}];
}
const row = rows[0];
const item = {
  id: row.id,
  tenantId: row.tenant_id,
  memberId: row.member_id,
  type: row.type,
  dateDebut: row.date_debut,
  dateFin: row.date_fin,
  motif: row.motif,
  statut: row.statut,
  createdAt: row.created_at,
  updatedAt: row.updated_at
};

return [{json: {success: true, data: {item: item}}}];
