var raw = $input.first().json;
var body = raw.body || raw;
var data = body.data || body;
var tenantId = String(body.tenantId || data.tenantId || '');
var absenceId = String(data.id || data.absenceId || '');

function esc(v) {
  return v == null ? '' : String(v).replace(/'/g, "''");
}

const uuidRegex = /^[0-9a-f]{8}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{12}$/i;
if (!uuidRegex.test(tenantId) || !uuidRegex.test(absenceId)) {
  throw new Error('Invalid tenantId or absenceId');
}

const updates = [];

if (data.type) { updates.push('type = \'' + esc(data.type) + '\''); }
if (data.dateDebut) { updates.push('date_debut = \'' + esc(data.dateDebut) + '\''); }
if (data.dateFin) { updates.push('date_fin = \'' + esc(data.dateFin) + '\''); }
if (data.motif) { updates.push('motif = \'' + esc(data.motif) + '\''); }
if (data.statut) { updates.push('statut = \'' + esc(data.statut) + '\''); }

if (updates.length === 0) {
  throw new Error('No fields to update');
}

const query = 'UPDATE equipe_absences SET ' + updates.join(', ') + ' WHERE id = \'' + esc(absenceId) + '\' AND tenant_id = \'' + esc(tenantId) + '\' RETURNING id, tenant_id, member_id, type, date_debut, date_fin, motif, statut, created_at, updated_at';

return [{json: {query: query}}];

const rows = $input.first().json.queryResults || [];
if (rows.length === 0) {
  return [{json: {success: false, error: 'Not found or not updated'}}];
}
const row = rows[0];
const item = {
  id: row.id,
  tenantId: row.tenant_id,
  memberId: row.member_id,
  type: row.type,
  dateDebut: row.date_debut,
  dateFin: row.date_fin,
  motif: row.motif,
  statut: row.statut,
  createdAt: row.created_at,
  updatedAt: row.updated_at
};

return [{json: {success: true, data: {item: item}}}];
