var raw = $input.first().json;
var body = raw.body || raw;
var data = body.data || body;
var tenantId = String(body.tenantId || data.tenantId || '');

function esc(v) {
  return v == null ? '' : String(v).replace(/'/g, "''");
}

const uuidRegex = /^[0-9a-f]{8}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{12}$/i;
if (!uuidRegex.test(tenantId)) {
  throw new Error('Invalid tenantId');
}

const query = `
SELECT 
  (SELECT COUNT(*) FROM equipe_members WHERE tenant_id = '${esc(tenantId)}') as total_members,
  (SELECT COUNT(*) FROM equipe_members WHERE tenant_id = '${esc(tenantId)}' AND actif = true) as active_members,
  (SELECT COUNT(*) FROM equipe_absences WHERE tenant_id = '${esc(tenantId)}' AND DATE_TRUNC('month', date_debut) = DATE_TRUNC('month', NOW())) as absences_this_month,
  (SELECT COALESCE(AVG(heures_travaillees), 0) FROM equipe_pointages WHERE tenant_id = '${esc(tenantId)}' AND DATE_TRUNC('month', date) = DATE_TRUNC('month', NOW())) as avg_hours_worked
`;

return [{json: {query: query}}];

const rows = $input.first().json.queryResults || [];
if (rows.length === 0) {
  return [{json: {success: false, error: 'Failed to retrieve data'}}];
}
const row = rows[0];
const dashboard = {
  totalMembers: parseInt(row.total_members) || 0,
  activeMembers: parseInt(row.active_members) || 0,
  absencesThisMonth: parseInt(row.absences_this_month) || 0,
  avgHoursWorked: parseFloat(row.avg_hours_worked) || 0
};

return [{json: {success: true, data: dashboard}}];
