var raw = $input.first().json;
var body = raw.body || raw;
var data = body.data || body;
var tenantId = String(body.tenantId || data.tenantId || '');

function esc(v) {
  return v == null ? '' : String(v).replace(/'/g, "''");
}

const uuidRegex = /^[0-9a-f]{8}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{12}$/i;
if (!uuidRegex.test(tenantId)) {
  throw new Error('Invalid tenantId');
}

const fields = [];
const values = [];
fields.push('tenant_id');
values.push('\'' + esc(tenantId) + '\'');

if (data.nom) { fields.push('nom'); values.push('\'' + esc(data.nom) + '\''); }
if (data.prenom) { fields.push('prenom'); values.push('\'' + esc(data.prenom) + '\''); }
if (data.email) { fields.push('email'); values.push('\'' + esc(data.email) + '\''); }
if (data.poste) { fields.push('poste'); values.push('\'' + esc(data.poste) + '\''); }
if (data.departement) { fields.push('departement'); values.push('\'' + esc(data.departement) + '\''); }
if (data.contratType) { fields.push('contrat_type'); values.push('\'' + esc(data.contratType) + '\''); }
if (data.dateEmbauche) { fields.push('date_embauche'); values.push('\'' + esc(data.dateEmbauche) + '\''); }
if (data.salaireBase !== undefined && data.salaireBase !== null) { fields.push('salaire_base'); values.push(data.salaireBase); }
if (data.manager) { fields.push('manager'); values.push('\'' + esc(data.manager) + '\''); }
if (data.actif !== undefined && data.actif !== null) { fields.push('actif'); values.push(data.actif === true || data.actif === 'true' ? 'true' : 'false'); }
if (data.userId) { fields.push('user_id'); values.push('\'' + esc(data.userId) + '\''); }

const query = 'INSERT INTO equipe_members (' + fields.join(', ') + ') VALUES (' + values.join(', ') + ') RETURNING id, tenant_id, nom, prenom, email, poste, departement, contrat_type, date_embauche, salaire_base, manager, actif, user_id, created_at, updated_at';

return [{json: {query: query}}];

const rows = $input.first().json.queryResults || [];
if (rows.length === 0) {
  return [{json: {success: false, error: 'Failed to create'}}];
}
const row = rows[0];
const item = {
  id: row.id,
  tenantId: row.tenant_id,
  nom: row.nom,
  prenom: row.prenom,
  email: row.email,
  poste: row.poste,
  departement: row.departement,
  contratType: row.contrat_type,
  dateEmbauche: row.date_embauche,
  salaireBase: row.salaire_base,
  manager: row.manager,
  actif: row.actif,
  userId: row.user_id,
  createdAt: row.created_at,
  updatedAt: row.updated_at
};

return [{json: {success: true, data: {item: item}}}];
