var raw = $input.first().json;
var body = raw.body || raw;
var data = body.data || body;
var tenantId = String(body.tenantId || data.tenantId || '');
var memberId = String(data.id || data.memberId || '');

function esc(v) {
  return v == null ? '' : String(v).replace(/'/g, "''");
}

const uuidRegex = /^[0-9a-f]{8}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{12}$/i;
if (!uuidRegex.test(tenantId) || !uuidRegex.test(memberId)) {
  throw new Error('Invalid tenantId or memberId');
}

const query = 'SELECT id, tenant_id, nom, prenom, email, poste, departement, contrat_type, date_embauche, salaire_base, manager, actif, user_id, created_at, updated_at FROM equipe_members WHERE id = \'' + esc(memberId) + '\' AND tenant_id = \'' + esc(tenantId) + '\'';

return [{json: {query: query}}];

const rows = $input.first().json.queryResults || [];
if (rows.length === 0) {
  return [{json: {success: false, error: 'Not found'}}];
}
const row = rows[0];
const item = {
  id: row.id,
  tenantId: row.tenant_id,
  nom: row.nom,
  prenom: row.prenom,
  email: row.email,
  poste: row.poste,
  departement: row.departement,
  contratType: row.contrat_type,
  dateEmbauche: row.date_embauche,
  salaireBase: row.salaire_base,
  manager: row.manager,
  actif: row.actif,
  userId: row.user_id,
  createdAt: row.created_at,
  updatedAt: row.updated_at
};

return [{json: {success: true, data: {item: item}}}];
