var raw = $input.first().json;
var body = raw.body || raw;
var data = body.data || body;
var tenantId = String(body.tenantId || data.tenantId || '');

function esc(v) {
  return v == null ? '' : String(v).replace(/'/g, "''");
}

const uuidRegex = /^[0-9a-f]{8}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{12}$/i;
if (!uuidRegex.test(tenantId)) {
  throw new Error('Invalid tenantId');
}

let query = 'SELECT id, tenant_id, nom, prenom, email, poste, departement, contrat_type, date_embauche, salaire_base, manager, actif, user_id, created_at, updated_at FROM equipe_members WHERE tenant_id = \'' + esc(tenantId) + '\'';

if (data.departement) {
  query += ' AND departement = \'' + esc(data.departement) + '\'';
}
if (data.actif !== undefined && data.actif !== null) {
  query += ' AND actif = ' + (data.actif === true || data.actif === 'true' ? 'true' : 'false');
}
if (data.search) {
  const search = esc(data.search);
  query += " AND (nom ILIKE '%" + search + "%' OR prenom ILIKE '%" + search + "%' OR email ILIKE '%" + search + "%')";
}

query += ' ORDER BY nom, prenom ASC';

return [{json: {query: query}}];
