var raw = $input.first().json;
var body = raw.body || raw;
var data = body.data || body;
var tenantId = String(body.tenantId || data.tenantId || '');

function esc(v) {
  return v == null ? '' : String(v).replace(/'/g, "''");
}

const uuidRegex = /^[0-9a-f]{8}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{12}$/i;
if (!uuidRegex.test(tenantId)) {
  throw new Error('Invalid tenantId');
}

const fields = [];
const values = [];
fields.push('tenant_id');
values.push('\'' + esc(tenantId) + '\'');

if (data.memberId) { fields.push('member_id'); values.push('\'' + esc(data.memberId) + '\''); }
if (data.date) { fields.push('date'); values.push('\'' + esc(data.date) + '\''); }
if (data.heureArrivee) { fields.push('heure_arrivee'); values.push('\'' + esc(data.heureArrivee) + '\''); }
if (data.heureDepart) { fields.push('heure_depart'); values.push('\'' + esc(data.heureDepart) + '\''); }
if (data.heuresPause !== undefined && data.heuresPause !== null) { fields.push('heures_pause'); values.push(data.heuresPause); }
if (data.heuresTravaillees !== undefined && data.heuresTravaillees !== null) { fields.push('heures_travaillees'); values.push(data.heuresTravaillees); }
if (data.note) { fields.push('note'); values.push('\'' + esc(data.note) + '\''); }

const query = 'INSERT INTO equipe_pointages (' + fields.join(', ') + ') VALUES (' + values.join(', ') + ') RETURNING id, tenant_id, member_id, date, heure_arrivee, heure_depart, heures_pause, heures_travaillees, note, created_at, updated_at';

return [{json: {query: query}}];

const rows = $input.first().json.queryResults || [];
if (rows.length === 0) {
  return [{json: {success: false, error: 'Failed to create'}}];
}
const row = rows[0];
const item = {
  id: row.id,
  tenantId: row.tenant_id,
  memberId: row.member_id,
  date: row.date,
  heureArrivee: row.heure_arrivee,
  heureDepart: row.heure_depart,
  heuresPause: row.heures_pause,
  heuresTravaillees: row.heures_travaillees,
  note: row.note,
  createdAt: row.created_at,
  updatedAt: row.updated_at
};

return [{json: {success: true, data: {item: item}}}];
