var raw = $input.first().json;
var body = raw.body || raw;
var data = body.data || body;
var tenantId = String(body.tenantId || data.tenantId || '');
var pointageId = String(data.id || data.pointageId || '');

function esc(v) {
  return v == null ? '' : String(v).replace(/'/g, "''");
}

const uuidRegex = /^[0-9a-f]{8}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{12}$/i;
if (!uuidRegex.test(tenantId) || !uuidRegex.test(pointageId)) {
  throw new Error('Invalid tenantId or pointageId');
}

const updates = [];

if (data.date) { updates.push('date = \'' + esc(data.date) + '\''); }
if (data.heureArrivee) { updates.push('heure_arrivee = \'' + esc(data.heureArrivee) + '\''); }
if (data.heureDepart) { updates.push('heure_depart = \'' + esc(data.heureDepart) + '\''); }
if (data.heuresPause !== undefined && data.heuresPause !== null) { updates.push('heures_pause = ' + data.heuresPause); }
if (data.heuresTravaillees !== undefined && data.heuresTravaillees !== null) { updates.push('heures_travaillees = ' + data.heuresTravaillees); }
if (data.note) { updates.push('note = \'' + esc(data.note) + '\''); }

if (updates.length === 0) {
  throw new Error('No fields to update');
}

const query = 'UPDATE equipe_pointages SET ' + updates.join(', ') + ' WHERE id = \'' + esc(pointageId) + '\' AND tenant_id = \'' + esc(tenantId) + '\' RETURNING id, tenant_id, member_id, date, heure_arrivee, heure_depart, heures_pause, heures_travaillees, note, created_at, updated_at';

return [{json: {query: query}}];

const rows = $input.first().json.queryResults || [];
if (rows.length === 0) {
  return [{json: {success: false, error: 'Not found or not updated'}}];
}
const row = rows[0];
const item = {
  id: row.id,
  tenantId: row.tenant_id,
  memberId: row.member_id,
  date: row.date,
  heureArrivee: row.heure_arrivee,
  heureDepart: row.heure_depart,
  heuresPause: row.heures_pause,
  heuresTravaillees: row.heures_travaillees,
  note: row.note,
  createdAt: row.created_at,
  updatedAt: row.updated_at
};

return [{json: {success: true, data: {item: item}}}];
