var raw = $input.first().json;
var body = raw.body || raw;
var data = body.data || body;
var tenantId = String(body.tenantId || data.tenantId || '');

function esc(v) {
  return v == null ? '' : String(v).replace(/'/g, "''");
}

const uuidRegex = /^[0-9a-f]{8}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{12}$/i;
if (!uuidRegex.test(tenantId)) {
  throw new Error('Invalid tenantId');
}

let query = 'SELECT ep.id, ep.tenant_id, ep.member_id, ep.date, ep.heure_arrivee, ep.heure_depart, ep.heures_pause, ep.heures_travaillees, ep.note, ep.created_at, ep.updated_at, em.nom, em.prenom FROM equipe_pointages ep LEFT JOIN equipe_members em ON ep.member_id = em.id WHERE ep.tenant_id = \'' + esc(tenantId) + '\'';

if (data.membreId) {
  query += ' AND ep.member_id = \'' + esc(data.membreId) + '\'';
}
if (data.dateFrom) {
  query += ' AND ep.date >= \'' + esc(data.dateFrom) + '\'';
}
if (data.dateTo) {
  query += ' AND ep.date <= \'' + esc(data.dateTo) + '\'';
}

query += ' ORDER BY ep.date DESC';

return [{json: {query: query}}];
