const body = $input.first().json.body || $input.first().json;

if (!body.tenantId || !body.invoiceId) {
  throw new Error('tenantId and invoiceId are required');
}

return [{ json: { tenantId: body.tenantId, invoiceId: body.invoiceId } }];

const inv = $('05. Get Invoice').first().json;

if (!inv || !inv.id) {
  throw new Error('Facture non trouvée ou statut incompatible (brouillon/annulée)');
}

return [{ json: inv }];

const inv = $('06. Check Invoice').first().json;
const nextNum = $('07. Get Next Avoir Number').first().json.next_num || 1;
const year = new Date().getFullYear();
const numeroAvoir = 'AVO-' + year + '-' + String(nextNum).padStart(6, '0');

return [{ json: { ...inv, numeroAvoir } }];

// Original prepare logic
const _origFn = function() {
const avoir = $('09. Create Avoir').first().json;
const lines = $('10. Get Invoice Lines').all().map(item => item.json).filter(l => l.id);

if (lines.length === 0) {
  return [{ json: { avoirId: avoir.id, hasLines: false } }];
}

const lineInserts = lines.map(line => ({
  avoirId: avoir.id,
  codeArticle: line.code_article || null,
  designation: line.designation || '',
  quantite: line.quantite || 1,
  prixUnitaireHt: line.prix_unitaire_ht || 0,
  totalHt: line.total_ht || 0,
  ordre: line.ordre || 0
}));

return [{ json: { avoirId: avoir.id, lineInserts, hasLines: true } }];
};
const _origResult = _origFn();
const prev = _origResult[0].json;

const items = prev.lineInserts || [];

if (!prev.hasLines || items.length === 0) {
  return [{ json: { ...prev, query: 'SELECT 1', params: [] } }];
}

const params = [];
let idx = 1;

const values = items.map((item, i) => {
  const parentId = item.avoirId;
  const codeArticle = item.codeArticle || null;
  const designation = String(item.designation || '').substring(0, 500);
  const quantite = parseFloat(item.quantite) || 0;
  const prixUnitaireHt = parseFloat(item.prixUnitaireHt) || 0;
  const totalHt = parseFloat(item.totalHt) || 0;
  const ordre = parseInt(item.ordre) || i;

  params.push(parentId, codeArticle, designation, quantite, prixUnitaireHt, totalHt, ordre);
  const p = `(gen_random_uuid(), $$${idx}, $$${idx+1}, $$${idx+2}, $$${idx+3}, $$${idx+4}, $$${idx+5}, $$${idx+6})`;
  idx += 7;
  return p;
}).join(',\n');

const query = `INSERT INTO avoir_lines (id, avoir_id, code_article, designation, quantite, prix_unitaire_ht, total_ht, ordre) VALUES ${values} RETURNING id, avoir_id, code_article, designation, quantite, prix_unitaire_ht, total_ht, ordre`;

return [{ json: { ...prev, query, params } }];

const avoir = $('09. Create Avoir').first().json;
const invoiceUpdate = $('13. Update Invoice Annulee').first().json;

let comptaResult = null;
try {
  const compta = $('14. Call Compta Avoir').first().json;
  if (compta && compta.success) {
    comptaResult = compta.ecriture || compta;
  } else {
    comptaResult = { error: compta.error || compta.message || 'Erreur comptabilisation avoir' };
  }
} catch (e) {
  comptaResult = { error: 'Appel compta avoir échoué: ' + e.message };
}

return [{ json: {
  success: true,
  avoir: {
    id: avoir.id,
    tenantId: avoir.tenant_id,
    clientFinalId: avoir.client_final_id,
    invoiceId: avoir.invoice_id,
    numeroAvoir: avoir.numero_avoir,
    dateAvoir: avoir.date_avoir,
    montantHt: avoir.montant_ht,
    montantTtc: avoir.montant_ttc,
    tvaTaux: avoir.tva_taux,
    motif: avoir.motif,
    statut: avoir.statut,
    createdAt: avoir.created_at
  },
  invoiceStatut: 'annulee',
  comptabilisation: comptaResult
} }];
