const body = $input.first().json.body || $input.first().json;

if (!body.tenantId) {
  throw new Error('tenantId is required');
}

const montantHt = parseFloat(body.montantHt) || 0;
const tvaTaux = parseFloat(body.tvaTaux) || 20;
const montantTtc = Math.round(montantHt * (1 + tvaTaux / 100) * 100) / 100;

return [{ json: {
  tenantId: body.tenantId,
  clientFinalId: body.clientFinalId || null,
  type: body.type || 'facture_client_final',
  montantHt,
  montantTtc,
  tvaTaux,
  dateFacture: body.dateFacture || new Date().toISOString(),
  dateEcheance: body.dateEcheance || null,
  description: body.description || null,
  modePaiement: body.modePaiement || null,
  lienPdf: body.lienPdf || null,
  lines: body.lines || []
} }];

const parser = $('02. Parser').first().json;
const nextNum = $('05. Get Next Number').first().json.next_num || 1;
const year = new Date().getFullYear();
const numeroFacture = 'FAC-' + year + '-' + String(nextNum).padStart(6, '0');

// Calculer date d'échéance si pas fournie (J+30)
let dateEcheance = parser.dateEcheance;
if (!dateEcheance) {
  const d = new Date(parser.dateFacture);
  d.setDate(d.getDate() + 30);
  dateEcheance = d.toISOString();
}

return [{ json: { ...parser, numeroFacture, dateEcheance } }];

const invoice = $('07. Insert Invoice').first().json;
const parser = $('02. Parser').first().json;
const lines = parser.lines || [];

if (lines.length === 0) {
  return [{ json: { invoiceId: invoice.id, lineInserts: [], hasLines: false } }];
}

const lineInserts = lines.map((line, idx) => ({
  invoiceId: invoice.id,
  codeArticle: line.codeArticle || null,
  designation: line.designation || '',
  quantite: line.quantite || 1,
  prixUnitaireHt: line.prixUnitaireHt || line.prixUnitaireHT || 0,
  totalHt: (line.quantite || 1) * (line.prixUnitaireHt || line.prixUnitaireHT || 0),
  ordre: idx
}));

return [{ json: { invoiceId: invoice.id, lineInserts, hasLines: true } }];
