const body = $input.first().json.body || $input.first().json;

if (!body.tenantId) {
  throw new Error('tenantId is required');
}

const montantHt = parseFloat(body.montantHt) || 0;
const tvaTaux = parseFloat(body.tvaTaux) || 20;
const montantTtc = Math.round(montantHt * (1 + tvaTaux / 100) * 100) / 100;

return [{ json: {
  tenantId: body.tenantId,
  clientFinalId: body.clientFinalId || null,
  type: body.type || 'facture_client_final',
  montantHt,
  montantTtc,
  tvaTaux,
  dateFacture: body.dateFacture || new Date().toISOString(),
  dateEcheance: body.dateEcheance || null,
  description: body.description || null,
  modePaiement: body.modePaiement || null,
  lienPdf: body.lienPdf || null,
  lines: body.lines || []
} }];

const parser = $('02. Parser').first().json;
const nextNum = $('05. Get Next Number').first().json.next_num || 1;
const year = new Date().getFullYear();
const numeroFacture = 'FAC-' + year + '-' + String(nextNum).padStart(6, '0');

let dateEcheance = parser.dateEcheance;
if (!dateEcheance) {
  const d = new Date(parser.dateFacture);
  d.setDate(d.getDate() + 30);
  dateEcheance = d.toISOString();
}

return [{ json: { ...parser, numeroFacture, dateEcheance } }];

const d = $json;
function esc(v) { return v == null ? 'NULL' : "'" + String(v).replace(/'/g, "''") + "'"; }

const clientPart = d.clientFinalId ? esc(d.clientFinalId) : 'NULL';
const descPart = d.description ? esc(d.description) : 'NULL';
const modePart = d.modePaiement ? esc(d.modePaiement) : 'NULL';
const lienPart = d.lienPdf ? esc(d.lienPdf) : 'NULL';

const query = 'INSERT INTO invoices (id, tenant_id, client_final_id, type, numero_facture, date_facture, date_echeance, montant_ht, montant_ttc, tva_taux, description, mode_paiement, lien_pdf, statut, created_at, updated_at) VALUES (gen_random_uuid(), ' + esc(d.tenantId) + '::uuid, ' + clientPart + ', ' + esc(d.type) + ', ' + esc(d.numeroFacture) + ', ' + esc(d.dateFacture) + ', ' + esc(d.dateEcheance) + ', ' + d.montantHt + ', ' + d.montantTtc + ', ' + d.tvaTaux + ', ' + descPart + ', ' + modePart + ', ' + lienPart + ', ' + "'brouillon'" + ', NOW(), NOW()) RETURNING *';

return [{ json: { query, lines: d.lines || [] } }];

const invoice = $json;
const lines = $('07. Build Insert SQL').first().json.lines || [];

if (lines.length === 0) {
  return [{ json: { invoiceId: invoice.id, hasLines: false, query: 'SELECT 1' } }];
}

function esc(v) { return v == null ? 'NULL' : "'" + String(v).replace(/'/g, "''") + "'"; }

const values = lines.map(function(line, i) {
  var parentId = esc(invoice.id);
  var codeArticle = line.codeArticle ? esc(line.codeArticle) : 'NULL';
  var designation = esc(String(line.designation || '').substring(0, 500));
  var quantite = parseFloat(line.quantite) || 1;
  var prixUnitaireHt = parseFloat(line.prixUnitaireHt || line.prixUnitaireHT) || 0;
  var totalHt = Math.round(quantite * prixUnitaireHt * 100) / 100;
  var ordre = i;
  return '(gen_random_uuid(), ' + parentId + ', ' + codeArticle + ', ' + designation + ', ' + quantite + ', ' + prixUnitaireHt + ', ' + totalHt + ', ' + ordre + ')';
}).join(', ');

var query = 'INSERT INTO invoice_lines (id, invoice_id, code_article, designation, quantite, prix_unitaire_ht, total_ht, ordre) VALUES ' + values + ' RETURNING id, invoice_id, code_article, designation, quantite, prix_unitaire_ht, total_ht, ordre';

return [{ json: { invoiceId: invoice.id, hasLines: true, query: query } }];

const invoice = $('07b. Insert Invoice').first().json;

var comptaResult = null;
try {
  var compta = $('10. Call Compta').first().json;
  if (compta && compta.success) {
    comptaResult = compta.ecriture || compta;
  } else {
    comptaResult = { error: compta.error || compta.message || 'Erreur comptabilisation' };
  }
} catch (e) {
  comptaResult = { error: 'Appel compta echoue: ' + e.message };
}

return [{ json: {
  id: invoice.id,
  tenantId: invoice.tenant_id,
  clientFinalId: invoice.client_final_id,
  type: invoice.type,
  numeroFacture: invoice.numero_facture,
  dateFacture: invoice.date_facture,
  dateEcheance: invoice.date_echeance,
  montantHt: invoice.montant_ht,
  montantTtc: invoice.montant_ttc,
  tvaTaux: invoice.tva_taux,
  description: invoice.description,
  modePaiement: invoice.mode_paiement,
  statut: invoice.statut,
  lienPdf: invoice.lien_pdf,
  createdAt: invoice.created_at,
  updatedAt: invoice.updated_at,
  comptabilisation: comptaResult
} }];
