const body = $input.first().json.body || $input.first().json;

if (!body.tenantId) {
  throw new Error('tenantId is required');
}

const montantHt = parseFloat(body.montantHt) || 0;
const tvaTaux = parseFloat(body.tvaTaux) || 20;
const montantTtc = Math.round(montantHt * (1 + tvaTaux / 100) * 100) / 100;

return [{ json: {
  tenantId: body.tenantId,
  clientFinalId: body.clientFinalId || null,
  type: body.type || 'facture_client_final',
  montantHt,
  montantTtc,
  tvaTaux,
  dateFacture: body.dateFacture || new Date().toISOString(),
  dateEcheance: body.dateEcheance || null,
  description: body.description || null,
  modePaiement: body.modePaiement || null,
  lienPdf: body.lienPdf || null,
  lines: body.lines || []
} }];

const parser = $('02. Parser').first().json;
const nextNum = $('05. Get Next Number').first().json.next_num || 1;
const year = new Date().getFullYear();
const numeroFacture = 'FAC-' + year + '-' + String(nextNum).padStart(6, '0');

let dateEcheance = parser.dateEcheance;
if (!dateEcheance) {
  const d = new Date(parser.dateFacture);
  d.setDate(d.getDate() + 30);
  dateEcheance = d.toISOString();
}

return [{ json: { ...parser, numeroFacture, dateEcheance } }];

// Original prepare logic
const _origFn = function() {
const invoice = $('07. Insert Invoice').first().json;
const parser = $('02. Parser').first().json;
const lines = parser.lines || [];

if (lines.length === 0) {
  return [{ json: { invoiceId: invoice.id, lineInserts: [], hasLines: false } }];
}

const lineInserts = lines.map((line, idx) => ({
  invoiceId: invoice.id,
  codeArticle: line.codeArticle || null,
  designation: line.designation || '',
  quantite: line.quantite || 1,
  prixUnitaireHt: line.prixUnitaireHt || line.prixUnitaireHT || 0,
  totalHt: (line.quantite || 1) * (line.prixUnitaireHt || line.prixUnitaireHT || 0),
  ordre: idx
}));

return [{ json: { invoiceId: invoice.id, lineInserts, hasLines: true } }];
};
const _origResult = _origFn();
const prev = _origResult[0].json;

const items = prev.lineInserts || [];

if (!prev.hasLines || items.length === 0) {
  return [{ json: { ...prev, query: 'SELECT 1', params: [] } }];
}

const params = [];
let idx = 1;

const values = items.map((item, i) => {
  const parentId = item.invoiceId;
  const codeArticle = item.codeArticle || null;
  const designation = String(item.designation || '').substring(0, 500);
  const quantite = parseFloat(item.quantite) || 0;
  const prixUnitaireHt = parseFloat(item.prixUnitaireHt) || 0;
  const totalHt = parseFloat(item.totalHt) || 0;
  const ordre = parseInt(item.ordre) || i;

  params.push(parentId, codeArticle, designation, quantite, prixUnitaireHt, totalHt, ordre);
  const p = `(gen_random_uuid(), $$${idx}, $$${idx+1}, $$${idx+2}, $$${idx+3}, $$${idx+4}, $$${idx+5}, $$${idx+6})`;
  idx += 7;
  return p;
}).join(',\n');

const query = `INSERT INTO invoice_lines (id, invoice_id, code_article, designation, quantite, prix_unitaire_ht, total_ht, ordre) VALUES ${values} RETURNING id, invoice_id, code_article, designation, quantite, prix_unitaire_ht, total_ht, ordre`;

return [{ json: { ...prev, query, params } }];

const invoice = $('07. Insert Invoice').first().json;

let comptaResult = null;
try {
  const compta = $('10. Call Compta').first().json;
  if (compta && compta.success) {
    comptaResult = compta.ecriture || compta;
  } else {
    comptaResult = { error: compta.error || compta.message || 'Erreur comptabilisation' };
  }
} catch (e) {
  comptaResult = { error: 'Appel compta échoué: ' + e.message };
}

return [{ json: {
  id: invoice.id,
  tenantId: invoice.tenant_id,
  clientFinalId: invoice.client_final_id,
  type: invoice.type,
  numeroFacture: invoice.numero_facture,
  dateFacture: invoice.date_facture,
  dateEcheance: invoice.date_echeance,
  montantHt: invoice.montant_ht,
  montantTtc: invoice.montant_ttc,
  tvaTaux: invoice.tva_taux,
  description: invoice.description,
  modePaiement: invoice.mode_paiement,
  statut: invoice.statut,
  lienPdf: invoice.lien_pdf,
  createdAt: invoice.created_at,
  updatedAt: invoice.updated_at,
  comptabilisation: comptaResult
} }];
