const raw = $input.first().json;
const pj = (x) => { if (typeof x !== 'string') return x; try { return JSON.parse(x); } catch (e) { return null; } };
const isO = (v) => v !== null && typeof v === 'object' && !Array.isArray(v);
const g = (o, k1, k2) => { if (!isO(o)) return null; const v = o[k1]; if (v != null && v !== '') return v; if (k2 && o[k2] != null && o[k2] !== '') return o[k2]; return null; };
const payload = (raw && typeof raw === 'object' && raw.data && typeof raw.data === 'object') ? { ...raw.data, tenantId: raw.data.tenantId || raw.tenantId } : raw;
const data = isO(payload) ? payload : {};

const invoiceId = g(data, 'invoiceId', 'invoice_id');
const tenantId = g(data, 'tenantId', 'tenant_id') || g(raw, 'tenantId');
const baseUrl = (g(data, 'baseUrl', 'base_url') || 'https://api.talosprimes.com').toString().replace(/\/$/, '');

if (!invoiceId) return [{ json: { _error: true, message: 'invoiceId requis' } }];
if (!tenantId) return [{ json: { _error: true, message: 'tenantId requis' } }];

const safeInvoiceId = invoiceId.toString().replace(/'/g, "''");
const safeTenantId = tenantId.toString().replace(/'/g, "''");
const lienPdf = `${baseUrl}/api/invoices/${invoiceId}/pdf`;

return [{ json: { invoiceId, tenantId, safeInvoiceId, safeTenantId, baseUrl, lienPdf } }];

const updated = $input.first().json;
const parser = $('02. Parser').first().json;
return [{ json: {
  success: true,
  message: 'lienPdf enregistre en base',
  invoiceId: parser.invoiceId,
  lienPdf: parser.lienPdf,
  numeroFacture: updated.numero_facture || null
} }];
