var body = $input.first().json.body || $input.first().json;

if (!body.tenantId || !body.invoiceId) {
  throw new Error('tenantId and invoiceId are required');
}

return [{ json: { tenantId: body.tenantId, invoiceId: body.invoiceId } }];

var inv = $('05. Get Invoice').first().json;
var lines = $('06. Get Lines').all().map(function(item) { return item.json; }).filter(function(l) { return l.id; });
var devisItems = $('07. Get Devis').all().map(function(item) { return item.json; }).filter(function(d) { return d.id; });
var proformaItems = $('08. Get Proformas').all().map(function(item) { return item.json; }).filter(function(p) { return p.id; });
var bdcItems = $('09. Get BdC').all().map(function(item) { return item.json; }).filter(function(b) { return b.id; });

if (!inv || !inv.id) {
  return [{ json: { error: 'Facture non trouv√©e' } }];
}

var invoice = {
  id: inv.id,
  tenantId: inv.tenant_id,
  clientFinalId: inv.client_final_id,
  type: inv.type,
  numeroFacture: inv.numero_facture,
  dateFacture: inv.date_facture,
  dateEcheance: inv.date_echeance,
  montantHt: inv.montant_ht,
  montantTtc: inv.montant_ttc,
  tvaTaux: inv.tva_taux,
  description: inv.description,
  modePaiement: inv.mode_paiement,
  statut: inv.statut,
  lienPdf: inv.lien_pdf,
  idExternePaiement: inv.id_externe_paiement,
  createdAt: inv.created_at,
  updatedAt: inv.updated_at,
  clientFinal: inv.client_final_id ? {
    id: inv.client_final_id,
    nom: inv.client_nom || null,
    prenom: inv.client_prenom || null,
    email: inv.client_email || null,
    telephone: inv.client_telephone || null,
    raisonSociale: inv.client_raison_sociale || null
  } : null,
  lines: lines,
  devis: devisItems.map(function(d) { return { id: d.id, numeroDevis: d.numero_devis, dateDevis: d.date_devis, montantHt: d.montant_ht, montantTtc: d.montant_ttc, statut: d.statut }; }),
  proformas: proformaItems.map(function(p) { return { id: p.id, numeroProforma: p.numero_proforma, dateProforma: p.date_proforma, montantHt: p.montant_ht, montantTtc: p.montant_ttc, statut: p.statut }; }),
  bonsCommande: bdcItems.map(function(b) { return { id: b.id, numeroBdc: b.numero_bdc, dateBdc: b.date_bdc, montantHt: b.montant_ht, montantTtc: b.montant_ttc, statut: b.statut }; })
};

return [{ json: invoice }];
