const items=$input.all();
const marked=items.filter(function(i){return i.json&&i.json.id});
if(marked.length===0){
  return [{json:{totalMarked:0,message:'Aucune facture en retard detectee'}}];
}
var byTenant={};
marked.forEach(function(i){
  var tid=i.json.tenant_id;
  if(!byTenant[tid])byTenant[tid]=[];
  byTenant[tid].push({id:i.json.id,numero_facture:i.json.numero_facture,date_echeance:i.json.date_echeance,montant_ttc:i.json.montant_ttc});
});
return [{json:{totalMarked:marked.length,byTenant:byTenant,message:marked.length+' facture(s) marquee(s) en retard'}}];
