const raw=$input.first().json;
function pj(x){if(typeof x!=='string')return x;try{return JSON.parse(x)}catch(e){return null}}
function isO(v){return v!==null&&typeof v==='object'&&!Array.isArray(v)}
function g(o,k1,k2){if(!isO(o))return null;if(o[k1]!=null&&o[k1]!=='')return o[k1];if(k2&&o[k2]!=null&&o[k2]!=='')return o[k2];return null}
const payload=(raw&&typeof raw==='object'&&raw.data&&typeof raw.data==='object')?{...raw.data,tenantId:raw.data.tenantId||raw.tenantId}:raw;
const data=isO(payload)?payload:{};
const invoiceId=g(data,'invoiceId','invoice_id');
const tenantId=g(data,'tenantId','tenant_id')||g(raw,'tenantId');
const referencePayment=g(data,'referencePayment','reference_payment')||'';
if(!invoiceId||!tenantId)return [{json:{_error:true,message:'invoiceId et tenantId requis'}}];
return [{json:{invoiceId,tenantId,referencePayment}}];

const row=$input.first().json;
const inv=row.invoice;
if(!inv)return [{json:{success:true,message:'Paiement enregistre (facture non recuperee)'}}];
const parsed=typeof inv==='string'?JSON.parse(inv):inv;
return [{json:{success:true,message:'Paiement enregistre',invoice:parsed}}];
