const _raw=$input.first().json;
const raw=(_raw && _raw.body && typeof _raw.body === "object") ? _raw.body : _raw;
function pj(x){if(typeof x!=='string')return x;try{return JSON.parse(x)}catch(e){return null}}
function isO(v){return v!==null&&typeof v==='object'&&!Array.isArray(v)}
function g(o,k1,k2){if(!isO(o))return null;var v=o[k1];if(v!=null&&v!=='')return v;if(k2){v=o[k2];if(v!=null&&v!=='')return v}return null}
const payload=(raw&&typeof raw==='object'&&raw.data&&typeof raw.data==='object')?{...raw.data,tenantId:raw.data.tenantId||raw.tenantId}:raw;
const data=isO(payload)?payload:{};
var page=g(data,'page');
var limit=g(data,'limit');
page=(page!=null&&page!=='')?parseInt(String(page),10):1;
limit=(limit!=null&&limit!=='')?parseInt(String(limit),10):20;
if(isNaN(page)||page<1)page=1;
if(isNaN(limit)||limit<1||limit>100)limit=20;
const tenantId=g(data,'tenantId')||g(raw,'tenantId');
if(!tenantId)return [{json:{_error:true,message:'tenantId requis'}}];
return [{json:{tenantId,page,limit,offset:(page-1)*limit,statut:g(data,'statut')||null,clientFinalId:g(data,'clientFinalId','client_final_id')||null,dateFrom:g(data,'dateFrom','date_from')||null,dateTo:g(data,'dateTo','date_to')||null}}];

const p=$json;
var esc=function(s){return (s||'').replace(/'/g,"''");};
var w="tenant_id = '"+esc(p.tenantId)+"'";
if(p.statut)w+=" AND statut = '"+esc(p.statut)+"'";
if(p.clientFinalId)w+=" AND client_final_id = '"+esc(p.clientFinalId)+"'";
if(p.dateFrom)w+=" AND date_facture >= '"+esc(p.dateFrom)+"'";
if(p.dateTo)w+=" AND date_facture <= '"+esc(p.dateTo)+"'";
var listQuery="SELECT COALESCE(json_agg(row_to_json(sub)), '[]'::json) AS invoices FROM (SELECT id, type, tenant_id, client_final_id, numero_facture, date_facture, date_echeance, montant_ht, montant_ttc, tva_taux, statut, lien_pdf, created_at FROM invoices WHERE "+w+" ORDER BY date_facture DESC LIMIT "+p.limit+" OFFSET "+p.offset+") sub";
var countQuery="SELECT COUNT(*)::int AS total FROM invoices WHERE "+w;
return [{json:{listQuery,countQuery,page:p.page,limit:p.limit}}];

const queries=$('05. Construire requetes').first().json;
const listRow=$('06. SELECT factures').first().json;
const countRow=$input.first().json;
var invoices=listRow.invoices||[];
if(typeof invoices==='string')invoices=JSON.parse(invoices);
var total=countRow.total!=null?Number(countRow.total):invoices.length;
var totalPages=Math.ceil(total/queries.limit)||1;
return [{json:{invoices,count:invoices.length,total,page:queries.page,limit:queries.limit,totalPages}}];
