var body = $input.first().json.body || $input.first().json;
if (!body.tenantId) throw new Error('tenantId is required');
var page = parseInt(body.page) || 1;
var limit = parseInt(body.limit) || 20;
var offset = (page - 1) * limit;
return [{ json: { tenantId: String(body.tenantId), page: page, limit: limit, offset: offset, statut: body.statut || null, clientFinalId: body.clientFinalId || null, dateFrom: body.dateFrom || null, dateTo: body.dateTo || null } }];

var p = $('02. Parser').first().json;
function esc(v) { return v == null ? 'NULL' : "'" + String(v).replace(/'/g, "''") + "'"; }
var where = "i.tenant_id = " + esc(p.tenantId) + "::uuid AND i.numero_facture IS NOT NULL AND i.date_facture IS NOT NULL";
if (p.statut) where += " AND i.statut = " + esc(p.statut);
if (p.clientFinalId) where += " AND i.client_final_id = " + esc(p.clientFinalId) + "::uuid";
if (p.dateFrom) where += " AND i.date_facture >= " + esc(p.dateFrom);
if (p.dateTo) where += " AND i.date_facture <= " + esc(p.dateTo);
var countQuery = "SELECT COUNT(*) as total FROM invoices i WHERE " + where;
var listQuery = "SELECT i.id, i.tenant_id, i.client_final_id, i.type, i.numero_facture, i.date_facture, i.date_echeance, i.montant_ht, i.montant_ttc, i.tva_taux, i.description, i.mode_paiement, i.statut, i.lien_pdf, i.id_externe_paiement, i.created_at, i.updated_at, cf.nom as client_nom, cf.prenom as client_prenom, cf.email as client_email, cf.telephone as client_telephone, cf.raison_sociale as client_raison_sociale FROM invoices i LEFT JOIN client_finals cf ON i.client_final_id = cf.id WHERE " + where + " ORDER BY i.date_facture DESC LIMIT " + p.limit + " OFFSET " + p.offset;
return [{ json: { countQuery: countQuery, listQuery: listQuery } }];

var items = $('07. List Query').all().map(function(item) { return item.json; }).filter(function(i) { return i && i.id; });
var ids = items.map(function(i) { return i.id; });
if (ids.length === 0) return [{ json: { invoiceIds: [], hasIds: false, idsList: '' } }];
var idsList = ids.map(function(id) { return "'" + String(id).replace(/'/g, "''") + "'"; }).join(',');
return [{ json: { invoiceIds: ids, hasIds: true, idsList: idsList } }];

var items = $('07. List Query').all().map(function(item) { return item.json; }).filter(function(i) { return i && i.id; });
var countResult = $('06. Count Query').first().json;
var parser = $('02. Parser').first().json;
var total = parseInt(countResult.total) || 0;
var totalPages = Math.ceil(total / parser.limit) || 1;

var bdcByInvoice = {};
try {
  var bdcItems = $('09. Get BdC Links').all().map(function(item) { return item.json; }).filter(function(b) { return b && b.id; });
  for (var bi = 0; bi < bdcItems.length; bi++) {
    var b = bdcItems[bi];
    if (!bdcByInvoice[b.invoice_id]) bdcByInvoice[b.invoice_id] = [];
    bdcByInvoice[b.invoice_id].push({ id: b.id, numeroBdc: b.numero_bdc, statut: b.statut });
  }
} catch(e) { /* no bdc links */ }

var devisByInvoice = {};
try {
  var devisItems = $('10. Get Devis Links').all().map(function(item) { return item.json; }).filter(function(d) { return d && d.id; });
  for (var di = 0; di < devisItems.length; di++) {
    var d = devisItems[di];
    if (!devisByInvoice[d.invoice_id]) devisByInvoice[d.invoice_id] = [];
    devisByInvoice[d.invoice_id].push({ id: d.id, numeroDevis: d.numero_devis, statut: d.statut });
  }
} catch(e) { /* no devis links */ }

var proformasByInvoice = {};
try {
  var proItems = $('11. Get Proforma Links').all().map(function(item) { return item.json; }).filter(function(p) { return p && p.id; });
  for (var pi = 0; pi < proItems.length; pi++) {
    var pr = proItems[pi];
    if (!proformasByInvoice[pr.invoice_id]) proformasByInvoice[pr.invoice_id] = [];
    proformasByInvoice[pr.invoice_id].push({ id: pr.id, numeroProforma: pr.numero_proforma, statut: pr.statut });
  }
} catch(e) { /* no proforma links */ }

var invoices = items.map(function(i) {
  return {
    id: i.id,
    tenantId: i.tenant_id,
    clientFinalId: i.client_final_id,
    type: i.type,
    numeroFacture: i.numero_facture,
    dateFacture: i.date_facture,
    dateEcheance: i.date_echeance,
    montantHt: i.montant_ht,
    montantTtc: i.montant_ttc,
    tvaTaux: i.tva_taux,
    description: i.description,
    modePaiement: i.mode_paiement,
    statut: i.statut,
    lienPdf: i.lien_pdf,
    idExternePaiement: i.id_externe_paiement,
    createdAt: i.created_at,
    updatedAt: i.updated_at,
    clientFinal: i.client_final_id ? { id: i.client_final_id, nom: i.client_nom || null, prenom: i.client_prenom || null, email: i.client_email || null, telephone: i.client_telephone || null, raisonSociale: i.client_raison_sociale || null } : null,
    bonsCommande: bdcByInvoice[i.id] || [],
    devis: devisByInvoice[i.id] || [],
    proformas: proformasByInvoice[i.id] || []
  };
});

return [{ json: { invoices: invoices, count: invoices.length, total: total, page: parser.page, limit: parser.limit, totalPages: totalPages } }];
