const body = $input.first().json.body || $input.first().json;

if (!body.tenantId) {
  throw new Error('tenantId is required');
}

const page = parseInt(body.page) || 1;
const limit = parseInt(body.limit) || 20;
const offset = (page - 1) * limit;
const statut = body.statut || null;
const clientFinalId = body.clientFinalId || null;
const dateFrom = body.dateFrom || null;
const dateTo = body.dateTo || null;

return [{ json: { tenantId: body.tenantId, page, limit, offset, statut, clientFinalId, dateFrom, dateTo } }];

const items = $('06. List Query').all().map(item => item.json).filter(i => i.id);
const invoiceIds = items.map(i => i.id);
return [{ json: { invoiceIds, hasIds: invoiceIds.length > 0 } }];

const items = $('06. List Query').all().map(item => item.json).filter(i => i.id);
const countResult = $('05. Count Query').first().json;
const parser = $('02. Parser').first().json;
const total = parseInt(countResult.total) || 0;
const totalPages = Math.ceil(total / parser.limit) || 1;

const bdcItems = $('08. Get BdC Links').all().map(item => item.json).filter(b => b.id);
const bdcByInvoice = {};
for (const b of bdcItems) {
  if (!bdcByInvoice[b.invoice_id]) bdcByInvoice[b.invoice_id] = [];
  bdcByInvoice[b.invoice_id].push({ id: b.id, numeroBdc: b.numero_bdc, statut: b.statut });
}

const devisItems = $('09. Get Devis Links').all().map(item => item.json).filter(d => d.id);
const devisByInvoice = {};
for (const d of devisItems) {
  if (!devisByInvoice[d.invoice_id]) devisByInvoice[d.invoice_id] = [];
  devisByInvoice[d.invoice_id].push({ id: d.id, numeroDevis: d.numero_devis, statut: d.statut });
}

const proformaItems = $('10. Get Proforma Links').all().map(item => item.json).filter(p => p.id);
const proformasByInvoice = {};
for (const p of proformaItems) {
  if (!proformasByInvoice[p.invoice_id]) proformasByInvoice[p.invoice_id] = [];
  proformasByInvoice[p.invoice_id].push({ id: p.id, numeroProforma: p.numero_proforma, statut: p.statut });
}

const invoices = items.map(i => ({
  id: i.id,
  tenantId: i.tenant_id,
  clientFinalId: i.client_final_id,
  type: i.type,
  numeroFacture: i.numero_facture,
  dateFacture: i.date_facture,
  dateEcheance: i.date_echeance,
  montantHt: i.montant_ht,
  montantTtc: i.montant_ttc,
  tvaTaux: i.tva_taux,
  description: i.description,
  modePaiement: i.mode_paiement,
  statut: i.statut,
  lienPdf: i.lien_pdf,
  idExternePaiement: i.id_externe_paiement,
  createdAt: i.created_at,
  updatedAt: i.updated_at,
  clientFinal: i.client_final_id ? {
    id: i.client_final_id,
    nom: i.client_nom || null,
    prenom: i.client_prenom || null,
    email: i.client_email || null,
    telephone: i.client_telephone || null,
    raisonSociale: i.client_raison_sociale || null
  } : null,
  bonsCommande: bdcByInvoice[i.id] || [],
  devis: devisByInvoice[i.id] || [],
  proformas: proformasByInvoice[i.id] || []
}));

return [{ json: {
  invoices,
  count: invoices.length,
  total,
  page: parser.page,
  limit: parser.limit,
  totalPages
}}];
