const raw=$input.first().json;
function pj(x){if(typeof x!=='string')return x;try{return JSON.parse(x)}catch(e){return null}}
function isO(v){return v!==null&&typeof v==='object'&&!Array.isArray(v)}
function g(o,k1,k2){if(!isO(o))return null;var v=o[k1];if(v!=null&&v!=='')return v;if(k2){v=o[k2];if(v!=null&&v!=='')return v}return null}
let payload=isO(raw)?raw:{};
if(isO(raw)&&raw.body!==undefined)payload=typeof raw.body==='string'?pj(raw.body):raw.body;
if(!isO(payload))payload={};
let data=isO(payload.data)?payload.data:payload;
if(!isO(data))data=payload;
var page=g(data,'page')||g(payload,'page');
var limit=g(data,'limit')||g(payload,'limit');
page=(page!=null&&page!=='')?parseInt(String(page),10):1;
limit=(limit!=null&&limit!=='')?parseInt(String(limit),10):20;
if(isNaN(page)||page<1)page=1;
if(isNaN(limit)||limit<1||limit>100)limit=20;
const tenantId=g(payload,'tenantId')||g(data,'tenantId')||g(raw,'tenantId');
if(!tenantId)return [{json:{_error:true,message:'tenantId requis'}}];
return [{json:{tenantId,page,limit,offset:(page-1)*limit,statut:g(data,'statut')||g(payload,'statut')||null,clientFinalId:g(data,'clientFinalId','client_final_id')||g(payload,'clientFinalId')||null,dateFrom:g(data,'dateFrom','date_from')||g(payload,'dateFrom')||null,dateTo:g(data,'dateTo','date_to')||g(payload,'dateTo')||null}}];

const p = $input.first().json;
const t = p.tenantId;
const w = ["i.tenant_id = '" + t + "'"];
if (p.statut) w.push("i.statut = '" + p.statut + "'");
if (p.clientFinalId) w.push("i.client_final_id = '" + p.clientFinalId + "'");
if (p.type) w.push("i.type = '" + p.type + "'");
const where = w.join(' AND ');

const listQuery = "SELECT COALESCE(json_agg(row_to_json(sub)), '[]'::json) AS invoices FROM ("
  + "SELECT i.id, i.type, i.tenant_id, i.client_final_id, i.numero_facture, i.date_facture, "
  + "i.date_echeance, i.montant_ht, i.montant_ttc, i.tva_taux, i.statut, i.lien_pdf, i.created_at, "
  + "cf.nom as client_nom, cf.prenom as client_prenom, cf.email as client_email, "
  + "cf.telephone as client_telephone, cf.raison_sociale as client_raison_sociale "
  + "FROM invoices i "
  + "LEFT JOIN client_finals cf ON i.client_final_id = cf.id "
  + "WHERE " + where
  + " ORDER BY i.date_facture DESC LIMIT " + p.limit + " OFFSET " + p.offset + ") sub";

const countQuery = "SELECT COUNT(*) as total FROM invoices i WHERE " + where;

return [{json: {listQuery, countQuery, page: p.page, limit: p.limit, offset: p.offset}}];

const queries = $('05. Construire requetes').first().json;
const listRow = $('06. SELECT factures').first().json;
const countRow = $input.first().json;
var invoices = listRow.invoices || [];
if (typeof invoices === 'string') invoices = JSON.parse(invoices);
var total = countRow.total != null ? Number(countRow.total) : invoices.length;
var totalPages = Math.ceil(total / queries.limit) || 1;

// Nest client data into clientFinal object
invoices = invoices.map(inv => ({
  ...inv,
  clientFinal: inv.client_final_id ? {
    id: inv.client_final_id,
    nom: inv.client_nom || null,
    prenom: inv.client_prenom || null,
    email: inv.client_email || null,
    telephone: inv.client_telephone || null,
    raisonSociale: inv.client_raison_sociale || null,
  } : null,
}));

return [{json: {invoices, count: invoices.length, total, page: queries.page, limit: queries.limit, totalPages}}];
