// n8n Webhook output met le payload dans $json.body
// Payload backend attendu (dans body): { event, tenantId, timestamp, data, metadata }
var raw = $input.first().json;
var payload = (raw && typeof raw === 'object' && 'body' in raw) ? raw.body : raw;
var data = (payload && typeof payload === 'object' && 'data' in payload) ? payload.data : payload;

var tenantId = String(payload?.tenantId ?? '00000000-0000-0000-0000-000000000001').trim();
var nom = String(data?.nom ?? '').trim();
var prenom = String(data?.prenom ?? '').trim();
var email = String(data?.email ?? '').trim();
var telephone = String(data?.telephone ?? '').trim();
var source = String(data?.source ?? 'admin').trim() || 'admin';
var notes = data?.notes ?? undefined;

if (!nom || !prenom || !email || !telephone) {
  throw new Error('Champs requis manquants (nom, prenom, email, telephone)');
}

return [{ json: { tenantId, nom, prenom, email, telephone, source, notes } }];

var d = $json;
// Escape single quotes
var esc = (v) => v ? String(v).replace(/'/g, "''") : '';
var query = `INSERT INTO leads (id, tenant_id, nom, prenom, email, telephone, source, notes, statut, created_at, updated_at)
VALUES (
  gen_random_uuid(),
  '${esc(d.tenantId)}'::uuid,
  '${esc(d.nom)}',
  '${esc(d.prenom)}',
  '${esc(d.email)}',
  '${esc(d.telephone)}',
  '${esc(d.source || 'admin')}',
  ${d.notes ? "'" + esc(d.notes) + "'" : 'NULL'},
  'nouveau',
  NOW(), NOW()
) ON CONFLICT (email) DO UPDATE SET nom = EXCLUDED.nom, prenom = EXCLUDED.prenom, telephone = EXCLUDED.telephone, updated_at = NOW() RETURNING *`;
return [{json: {query}}];
