// Parser le payload: { body: { data: { id } } }
var raw = $input.first().json;
var payload = (raw && typeof raw === 'object' && 'body' in raw) ? raw.body : raw;
var data = (payload && typeof payload === 'object' && 'data' in payload) ? payload.data : (payload ?? {});

var id = data?.id ? String(data.id).trim() : null;

if (!id) {
  throw new Error('ID du lead requis');
}

return [{ json: { id } }];

var items = $input.all();
if (!items || items.length === 0 || !items[0].json?.id) {
  return [{ json: { success: false, error: 'Lead non trouvé' } }];
}
var lead = items[0].json;
return [{ json: { success: true, data: { lead: { id: lead.id, tenantId: lead.tenant_id, nom: lead.nom, prenom: lead.prenom, email: lead.email, telephone: lead.telephone, statut: lead.statut, source: lead.source, notes: lead.notes ?? null, dateContact: lead.date_contact ?? null, dateEntretien: lead.date_entretien ?? null, typeEntretien: lead.type_entretien ?? null, createdAt: lead.created_at, updatedAt: lead.updated_at } } } }];
