// Parser le payload: { body: { data: { id } } }
const raw = $input.first().json;
const payload = (raw && typeof raw === 'object' && 'body' in raw) ? raw.body : raw;
const data = (payload && typeof payload === 'object' && 'data' in payload) ? payload.data : (payload ?? {});

const id = data?.id ? String(data.id).trim() : null;

if (!id) {
  throw new Error('ID du lead requis');
}

return [{ json: { id } }];

// Formater la réponse: { lead: {...} }
const items = $input.all();

if (!items || items.length === 0 || !items[0].json?.id) {
  return [{ json: { success: false, error: 'Lead non trouvé' } }];
}

const lead = items[0].json;

return [{ 
  json: { 
    success: true, 
    data: { 
      lead: {
        id: lead.id,
        nom: lead.nom,
        prenom: lead.prenom,
        email: lead.email,
        telephone: lead.telephone,
        statut: lead.statut,
        source: lead.source,
        notes: lead.notes ?? null,
        dateContact: lead.date_contact ?? null,
        createdAt: lead.created_at,
        updatedAt: lead.updated_at
      }
    } 
  } 
}];
