const raw = $input.first().json;
const payload = (raw && typeof raw === 'object' && 'body' in raw) ? raw.body : raw;
const data = (payload && typeof payload === 'object' && 'data' in payload) ? payload.data : (payload ?? {});

const id = data?.id ? String(data.id) : '';
const statut = data?.statut ? String(data.statut) : '';
const VALID = ['nouveau', 'contacte', 'qualifie', 'converti', 'perdu'];
if (!id) throw new Error('ID requis');
if (!statut) throw new Error('Statut requis');
if (!VALID.includes(statut)) throw new Error('Statut invalide: ' + statut + '. Valeurs: ' + VALID.join(', '));
return [{ json: { id, statut } }];
