// Parser le payload avec validation whitelist (anti-injection SQL)
const raw = $input.first().json;
const payload = (raw && typeof raw === 'object' && 'body' in raw) ? raw.body : raw;
const tenantId = String(payload?.tenantId || '').trim();
const data = (payload && typeof payload === 'object' && 'data' in payload) ? payload.data : (payload ?? {});

// Validation UUID du tenantId
const UUID_RE = /^[0-9a-f]{8}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{12}$/i;
if (!tenantId || !UUID_RE.test(tenantId)) throw new Error('Tenant ID requis et doit être un UUID valide');

// Whitelist des valeurs autorisées
const VALID_SOURCES = ['site_web', 'telephone', 'email', 'recommandation', 'salon', 'publicite', 'reseaux_sociaux', 'autre', 'telephone_ia'];
const VALID_STATUTS = ['nouveau', 'contacte', 'qualifie', 'converti', 'perdu'];

const rawSource = data?.source ? String(data.source).trim().toLowerCase() : null;
const rawStatut = data?.statut ? String(data.statut).trim().toLowerCase() : null;
const rawLimit = data?.limit ? parseInt(String(data.limit), 10) : 100;

const source = rawSource && VALID_SOURCES.includes(rawSource) ? rawSource : null;
const statut = rawStatut && VALID_STATUTS.includes(rawStatut) ? rawStatut : null;
const limit = (Number.isFinite(rawLimit) && rawLimit > 0 && rawLimit <= 500) ? rawLimit : 100;

// Construire la requête paramétrisée dynamiquement
const conditions = [];
conditions.push(`tenant_id = '${tenantId}'::uuid`);

if (source) {
  conditions.push(`source = '${source}'`);
}
if (statut) {
  conditions.push(`statut = '${statut}'`);
}

const whereClause = conditions.join(' AND ');
const query = `SELECT id, tenant_id, nom, prenom, email, telephone, entreprise, fonction, statut, source, notes, tags, score, created_at, updated_at
FROM leads
WHERE ${whereClause}
ORDER BY created_at DESC
LIMIT ${limit}`;

return [{ json: { query } }];

const items = $input.all();
if (!items || items.length === 0 || !items[0].json?.id) {
  return [{ json: { leads: [] } }];
}
const leads = items.map(item => {
  const lead = item.json;
  return { id: lead.id, tenantId: lead.tenant_id, nom: lead.nom, prenom: lead.prenom, email: lead.email, telephone: lead.telephone, entreprise: lead.entreprise ?? null, fonction: lead.fonction ?? null, statut: lead.statut, source: lead.source, notes: lead.notes ?? null, tags: lead.tags ?? [], score: lead.score ?? 0, createdAt: lead.created_at, updatedAt: lead.updated_at };
});
return [{ json: { leads } }];
