// Parser le payload: { body: { event, tenantId, timestamp, data: { source?, statut?, limit? } } }
const raw = $input.first().json;
const payload = (raw && typeof raw === 'object' && raw.data && typeof raw.data === 'object') ? { ...raw.data, tenantId: raw.data.tenantId || raw.tenantId } : raw;
// Le backend envoie { event, tenantId, timestamp, data: { source, statut, limit } }
const data = (payload && typeof payload === 'object' && 'data' in payload) ? payload.data : (payload ?? {});

const source = data?.source ? String(data.source).trim() : null;
const statut = data?.statut ? String(data.statut).trim() : null;
const limit = data?.limit ? parseInt(String(data.limit), 10) : 100;

return [{ json: { source, statut, limit } }];

// Formater la réponse: { leads: [...] }
const items = $input.all();
// Si aucun résultat, retourner un tableau vide
if (!items || items.length === 0) {
  return [{ json: { leads: [] } }];
}

const leads = items.map(item => {
  const lead = item.json;
  return {
    id: lead.id,
    nom: lead.nom,
    prenom: lead.prenom,
    email: lead.email,
    telephone: lead.telephone,
    statut: lead.statut,
    source: lead.source,
    notes: lead.notes ?? null,
    dateContact: lead.date_contact ?? null,
    createdAt: lead.created_at,
    updatedAt: lead.updated_at
  };
});

// Retourner directement { leads } pour que le backend puisse lire result.data?.leads
return [{ json: { leads } }];
