const body = $input.first().json.body || $input.first().json;

if (!body.tenantId) {
  throw new Error('tenantId is required');
}

const page = parseInt(body.page) || 1;
const limit = parseInt(body.limit) || 10;
const offset = (page - 1) * limit;
const workflow = body.workflow || null;
const typeEvenement = body.typeEvenement || null;
const statutExecution = body.statutExecution || null;

return [{ json: {
  tenantId: body.tenantId,
  page,
  limit,
  offset,
  workflow,
  typeEvenement,
  statutExecution
} }];

const logItems = $('06. List Query').all();
const logs = logItems.map(item => item.json).filter(l => l.id);
const countResult = $('05. Count Query').first().json;
const total = parseInt(countResult.total) || 0;
const totalPages = Math.ceil(total / $('02. Parser').first().json.limit) || 1;

return [{ json: {
  logs: logs,
  count: logs.length,
  total: total,
  page: $('02. Parser').first().json.page,
  limit: $('02. Parser').first().json.limit,
  totalPages: totalPages
} }];
