var body = $input.first().json.body || $input.first().json;

if (!body.tenantId) {
  throw new Error('tenantId is required');
}

var page = parseInt(body.page) || 1;
var limit = parseInt(body.limit) || 10;
var offset = (page - 1) * limit;
var workflow = body.workflow || null;
var typeEvenement = body.typeEvenement || null;
var statutExecution = body.statutExecution || null;

return [{ json: {
  tenantId: body.tenantId,
  page,
  limit,
  offset,
  workflow,
  typeEvenement,
  statutExecution
} }];

var logItems = $('06. List Query').all();
var logs = logItems.map(function(item) { return item.json; }).filter(function(l) { return l.id; });
var countResult = $('05. Count Query').first().json;
var total = parseInt(countResult.total) || 0;
var totalPages = Math.ceil(total / $('02. Parser').first().json.limit) || 1;

return [{ json: {
  logs: logs,
  count: logs.length,
  total: total,
  page: $('02. Parser').first().json.page,
  limit: $('02. Parser').first().json.limit,
  totalPages: totalPages
} }];
