const body = $input.first().json.body || $input.first().json;

if (!body.tenantId || !body.proformaId) {
  throw new Error('tenantId and proformaId are required');
}

return [{ json: { tenantId: body.tenantId, proformaId: body.proformaId } }];

const proforma = $('05. Get Proforma').first().json;

if (!proforma || !proforma.id) {
  throw new Error('Proforma not found or not in acceptee status');
}

return [{ json: proforma }];

const proforma = $('06. Check Proforma').first().json;
const nextNum = $('07. Get Next Invoice Number').first().json.next_num || 1;
const year = new Date().getFullYear();
const numeroFacture = 'INV-' + year + '-' + String(nextNum).padStart(6, '0');

return [{ json: {
  ...proforma,
  numeroFacture,
  dateFacture: new Date().toISOString()
} }];

// Original prepare logic
const _origFn = function() {
const invoice = $('09. Create Invoice').first().json;
const lines = $('10. Get Proforma Lines').all().map(item => item.json);

if (lines.length === 0) {
  return [{ json: { invoiceId: invoice.id, hasLines: false } }];
}

const lineInserts = lines.map(line => ({
  invoiceId: invoice.id,
  codeArticle: line.code_article || null,
  designation: line.designation || '',
  quantite: line.quantite || 1,
  prixUnitaireHt: line.prix_unitaire_ht || 0,
  totalHt: line.total_ht || 0,
  ordre: line.ordre || 0
}));

return [{ json: { invoiceId: invoice.id, lineInserts, hasLines: true } }];
};
const _origResult = _origFn();
const prev = _origResult[0].json;

const items = prev.lineInserts || [];

if (!prev.hasLines || items.length === 0) {
  return [{ json: { ...prev, query: 'SELECT 1', params: [] } }];
}

const params = [];
let idx = 1;

const values = items.map((item, i) => {
  const parentId = item.invoiceId;
  const codeArticle = item.codeArticle || null;
  const designation = String(item.designation || '').substring(0, 500);
  const quantite = parseFloat(item.quantite) || 0;
  const prixUnitaireHt = parseFloat(item.prixUnitaireHt) || 0;
  const totalHt = parseFloat(item.totalHt) || 0;
  const ordre = parseInt(item.ordre) || i;

  params.push(parentId, codeArticle, designation, quantite, prixUnitaireHt, totalHt, ordre);
  const p = `(gen_random_uuid(), $$${idx}, $$${idx+1}, $$${idx+2}, $$${idx+3}, $$${idx+4}, $$${idx+5}, $$${idx+6})`;
  idx += 7;
  return p;
}).join(',
');

const query = `INSERT INTO invoice_lines (id, invoice_id, code_article, designation, quantite, prix_unitaire_ht, total_ht, ordre) VALUES ${values} RETURNING id, invoice_id, code_article, designation, quantite, prix_unitaire_ht, total_ht, ordre`;

return [{ json: { ...prev, query, params } }];
