const raw = $input.first().json;
const body = (raw && raw.body && typeof raw.body === "object") ? raw.body : raw;
const payload = (body && body.data && typeof body.data === "object") ? { ...body.data, tenantId: body.data.tenantId || body.tenantId } : body;

if (!payload.tenantId || !payload.clientFinalId || !payload.montantHt || !payload.tvaTaux) {
  throw new Error('tenantId, clientFinalId, montantHt, tvaTaux are required');
}

const montantHt = parseFloat(payload.montantHt);
const tvaTaux = parseFloat(payload.tvaTaux) / 100;
const montantTtc = montantHt * (1 + tvaTaux);
const dateProforma = payload.dateProforma || new Date().toISOString().split('T')[0];
const dateValidite = payload.dateValidite || new Date(new Date().getTime() + 30 * 24 * 60 * 60 * 1000).toISOString().split('T')[0];
const description = payload.description || '';
const modePaiement = payload.modePaiement || 'virement';
const lines = (payload.lines || []).map(line => ({
  codeArticle: line.codeArticle || line.code_article || '',
  designation: line.designation || line.description || '',
  quantite: parseInt(line.quantite || line.qty || 1),
  prixUnitaireHt: parseFloat(line.prixUnitaireHt || line.prix_unitaire_ht || line.prixUnitaire || 0),
  totalHt: parseFloat(line.totalHt || line.total_ht || line.montantHt || 0),
  ordre: parseInt(line.ordre || 0)
}));

return {
  tenantId: payload.tenantId,
  clientFinalId: payload.clientFinalId,
  montantHt: montantHt,
  montantTtc: montantTtc,
  tvaTaux: payload.tvaTaux,
  dateProforma: dateProforma,
  dateValidite: dateValidite,
  description: description,
  modePaiement: modePaiement,
  lines: lines
};

const nextNum = $('05. Get Next Number').first().next_num;
const numeroProforma = 'PRO-' + new Date().getFullYear() + '-' + String(nextNum).padStart(6, '0');

return {
  ...JSON.parse(JSON.stringify($json)),
  numeroProforma: numeroProforma
};

const proformaId = $('07. Insert Proforma').first().id;
const lines = $json.lines || [];

if (lines.length === 0) {
  return {
    proformaId: proformaId,
    hasLines: false,
    insertLinesSql: ''
  };
}

const valueParts = lines.map((line, index) => {
  const codeArticle = (line.codeArticle || '').replace(/'/g, "''");
  const designation = (line.designation || '').replace(/'/g, "''");
  const quantite = parseInt(line.quantite || 1);
  const prixUnitaireHt = parseFloat(line.prixUnitaireHt || 0);
  const totalHt = parseFloat(line.totalHt || 0);
  const ordre = parseInt(line.ordre || index);

  return `('${proformaId}', '${codeArticle}', '${designation}', ${quantite}, ${prixUnitaireHt}, ${totalHt}, ${ordre})`;
}).join(',\n  ');

const insertLinesSql = `INSERT INTO proforma_lines (proforma_id, code_article, designation, quantite, prix_unitaire_ht, total_ht, ordre) VALUES\n  ${valueParts}`;

return {
  proformaId: proformaId,
  hasLines: true,
  insertLinesSql: insertLinesSql
};

return {
  success: true,
  message: 'Proforma created successfully',
  proformaId: $('07. Insert Proforma').first().id,
  numeroProforma: $json.numeroProforma
};
