var body = $input.first().json.body || $input.first().json;

if (!body.tenantId) {
  throw new Error('tenantId is required');
}

var montantHt = parseFloat(body.montantHt) || 0;
var tvaTaux = parseFloat(body.tvaTaux) || 20;
var montantTtc = Math.round(montantHt * (1 + tvaTaux / 100) * 100) / 100;

return [{ json: {
  tenantId: body.tenantId,
  clientFinalId: body.clientFinalId || null,
  montantHt,
  montantTtc,
  tvaTaux,
  dateProforma: body.dateProforma || new Date().toISOString(),
  dateValidite: body.dateValidite || null,
  description: body.description || null,
  modePaiement: body.modePaiement || null,
  lines: body.lines || []
} }];

var parser = $('02. Parser').first().json;
var nextNum = $('05. Get Next Number').first().json.next_num || 1;
var year = new Date().getFullYear();
var numeroProforma = 'PRO-' + year + '-' + String(nextNum).padStart(6, '0');

return [{ json: { ...parser, numeroProforma } }];

// Original prepare logic
var _origFn = function() {
var proforma = $('07. Insert Proforma').first().json;
var parser = $('02. Parser').first().json;
var lines = parser.lines || [];

if (lines.length === 0) {
  return [{ json: { proformaId: proforma.id, lineInserts: [], hasLines: false } }];
}

var lineInserts = lines.map(function(line, idx) { return ({
  proformaId: proforma.id,
  codeArticle: line.codeArticle || null,
  designation: line.designation || '',
  quantite: line.quantite || 1,
  prixUnitaireHt: line.prixUnitaireHt || line.prixUnitaireHT || 0,
  totalHt: (line.quantite || 1) * (line.prixUnitaireHt || line.prixUnitaireHT || 0),
  ordre: idx
}); });

return [{ json: { proformaId: proforma.id, lineInserts, hasLines: true } }];
};
var _origResult = _origFn();
var prev = _origResult[0].json;

var items = prev.lineInserts || [];

if (!prev.hasLines || items.length === 0) {
  return [{ json: { ...prev, query: 'SELECT 1', params: [] } }];
}

var params = [];
var idx = 1;

var values = items.map(function(item, i) { return {
  var parentId = item.proformaId;
  var codeArticle = item.codeArticle || null;
  var designation = String(item.designation || '').substring(0, 500);
  var quantite = parseFloat(item.quantite) || 0;
  var prixUnitaireHt = parseFloat(item.prixUnitaireHt) || 0;
  var totalHt = parseFloat(item.totalHt) || 0;
  var ordre = parseInt(item.ordre) || i;

  params.push(parentId, codeArticle, designation, quantite, prixUnitaireHt, totalHt, ordre);
  var p = `(gen_random_uuid(), $$${idx}, $$${idx+1}, $$${idx+2}, $$${idx+3}, $$${idx+4}, $$${idx+5}, $$${idx+6})`;
  idx += 7;
  return p;
}; }).join(',
');

var query = `INSERT INTO proforma_lines (id, proforma_id, code_article, designation, quantite, prix_unitaire_ht, total_ht, ordre) VALUES ${values} RETURNING id, proforma_id, code_article, designation, quantite, prix_unitaire_ht, total_ht, ordre`;

return [{ json: { ...prev, query, params } }];
