const payload = $input.body;

if (!payload.tenantId) {
  throw new Error('tenantId is required');
}

const page = parseInt(payload.page) || 1;
const limit = parseInt(payload.limit) || 10;
const offset = (page - 1) * limit;
const statut = payload.statut || null;
const clientFinalId = payload.clientFinalId || null;

return {
  tenantId: payload.tenantId,
  page,
  limit,
  offset,
  statut,
  clientFinalId
};

parameters.jsCode:
const rawProforma = $('06. List Query').all().map(item => item.json);
const countResult = $('05. Count Query').first().json;
const total = parseInt(countResult.total) || 0;
const totalPages = Math.ceil(total / $json.limit) || 1;

// Nest client data into clientFinal object
const proforma = rawProforma.map(p => ({
  ...p,
  clientFinal: p.client_final_id ? {
    id: p.client_final_id,
    nom: p.client_nom || null,
    prenom: p.client_prenom || null,
    email: p.client_email || null,
    telephone: p.client_telephone || null,
    raisonSociale: p.client_raison_sociale || null,
  } : null,
}));

return [{json: {
  proforma,
  count: proforma.length,
  total,
  page: $json.page,
  limit: $json.limit,
  totalPages,
}}];
